   138→<script>
   139→    // Check authentication state and update header
   140→    document.addEventListener('DOMContentLoaded', function() {
   141→        checkHeaderAuthState();
   142→        checkPremiumMembershipForAds();
   143→    });
   144→
   145→    function checkHeaderAuthState() {
   146→        // Check for user data in localStorage
   147→        const userData = localStorage.getItem('user');
   148→        
   149→        if (userData) {
   150→            try {
   151→                const user = JSON.parse(userData);
   152→                showSignedInState(user);
   153→                showHeaderAccountWidget(user);
   154→            } catch (error) {
   155→                console.error('Error parsing user data:', error);
   156→                showSignedOutState();
   157→                hideHeaderAccountWidget();
   158→            }
   159→        } else {
   160→            showSignedOutState();
   161→            hideHeaderAccountWidget();
   162→        }
   163→    }
   164→
   165→    function showSignedInState(user) {
   166→        // Hide sign in/create account links
   167→        const signinLink = document.getElementById('signin-link');
   168→        const createAccountLink = document.getElementById('create-account-link');
   169→        const mobileSigninLink = document.getElementById('mobile-signin-link');
   170→        const mobileCreateAccountLink = document.getElementById('mobile-create-account-link');
   171→        
   172→        if (signinLink) signinLink.classList.add('hidden');
   173→        if (createAccountLink) createAccountLink.classList.add('hidden');
   174→        if (mobileSigninLink) mobileSigninLink.classList.add('hidden');
   175→        if (mobileCreateAccountLink) mobileCreateAccountLink.classList.add('hidden');
   176→        
   177→        // Show account and premium links
   178→        const accountLink = document.getElementById('account-link');
   179→        const premiumLink = document.getElementById('premium-link');
   180→        const mobileAccountLink = document.getElementById('mobile-account-link');
   181→        const mobilePremiumLink = document.getElementById('mobile-premium-link');
   182→        
   183→        if (accountLink) accountLink.classList.remove('hidden');
   184→        if (premiumLink) premiumLink.classList.remove('hidden');
   185→        if (mobileAccountLink) mobileAccountLink.classList.remove('hidden');
   186→        if (mobilePremiumLink) mobilePremiumLink.classList.remove('hidden');
   187→    }
   188→
   189→    function showSignedOutState() {
   190→        // Show sign in/create account links
   191→        const signinLink = document.getElementById('signin-link');
   192→        const createAccountLink = document.getElementById('create-account-link');
   193→        const mobileSigninLink = document.getElementById('mobile-signin-link');
   194→        const mobileCreateAccountLink = document.getElementById('mobile-create-account-link');
   195→        
   196→        if (signinLink) signinLink.classList.remove('hidden');
   197→        if (createAccountLink) createAccountLink.classList.remove('hidden');
   198→        if (mobileSigninLink) mobileSigninLink.classList.remove('hidden');
   199→        if (mobileCreateAccountLink) mobileCreateAccountLink.classList.remove('hidden');
   200→        
   201→        // Hide account and premium links
   202→        const accountLink = document.getElementById('account-link');
   203→        const premiumLink = document.getElementById('premium-link');
   204→        const mobileAccountLink = document.getElementById('mobile-account-link');
   205→        const mobilePremiumLink = document.getElementById('mobile-premium-link');
   206→        
   207→        if (accountLink) accountLink.classList.add('hidden');
   208→        if (premiumLink) premiumLink.classList.add('hidden');
   209→        if (mobileAccountLink) mobileAccountLink.classList.add('hidden');
   210→        if (mobilePremiumLink) mobilePremiumLink.classList.add('hidden');
   211→    }
   212→
   213→    function checkPremiumMembershipForAds() {
   214→        try {
   215→            // Check for membership data
   216→            const membershipData = localStorage.getItem('membership') || localStorage.getItem('eplMembership');
   217→            
   218→            if (membershipData) {
   219→                const membership = JSON.parse(membershipData);
   220→                
   221→                // If user has premium membership, hide ads
   222→                if (membership.plan && membership.plan !== 'free') {
   223→                    hidePremiumAds();
   224→                    showAdFreeIndicator();
   225→                }
   226→            }
   227→        } catch (error) {
   228→            console.error('Error checking membership for ads:', error);
   229→        }
   230→    }
   231→
   232→    function hidePremiumAds() {
   233→        // Hide Google AdSense ads
   234→        const adElements = document.querySelectorAll('.adsbygoogle, [class*="ad-"], [id*="ad-"], [class*="advertisement"], [class*="google-ads"]');
   235→        adElements.forEach(ad => {
   236→            ad.style.display = 'none';
   237→            ad.style.visibility = 'hidden';
   238→        });
   239→
   240→        // Also hide any ad containers or wrappers
   241→        const adContainers = document.querySelectorAll('[class*="ad-container"], [class*="ad-wrapper"], [id*="ad-container"], [id*="ad-wrapper"]');
   242→        adContainers.forEach(container => {
   243→            container.style.display = 'none';
   244→        });
   245→
   246→        // Add a class to body to indicate ad-free mode
   247→        document.body.classList.add('ad-free-premium');
   248→        
   249→        console.log('Premium ad-free experience activated');
   250→    }
