   952→        window.enableNotificationsFromPrompt = async function() {
   953→            try {
   954→                const granted = await notificationService.requestPermission();
   955→                if (granted) {
   956→                    dismissNotificationPrompt();
   957→                    // Show success message
   958→                    showNotificationMessage('✅ Notifications enabled! You\'ll now receive EPL news updates.', 'success');
   959→                } else {
   960→                    showNotificationMessage('❌ Notification permission was denied. You can enable it later in your browser settings.', 'error');
   961→                }
   962→            } catch (error) {
   963→                console.error('Failed to enable notifications:', error);
   964→                showNotificationMessage('❌ Failed to enable notifications. Please try again.', 'error');
   965→            }
   966→        }
   967→        
   968→        window.dismissNotificationPrompt = function() {
   969→            const prompt = document.getElementById('notification-prompt');
   970→            if (prompt) {
   971→                prompt.style.animation = 'slideInFromRight 0.3s ease-out reverse';
   972→                setTimeout(() => {
   973→                    prompt.remove();
   974→                }, 300);
   975→            }
   976→            localStorage.setItem('notification-prompt-dismissed', 'true');
