}

.account-btn.primary {
    background: linear-gradient(135deg, #37003c, #6f42c1);
    color: white;
}

.account-btn.primary:hover {
    background: linear-gradient(135deg, #6f42c1, #37003c);
    transform: translateY(-1px);
}

.account-btn.secondary {
    background: linear-gradient(135deg, #00ff87, #28a745);
    color: #000;
}

.account-btn.secondary:hover {
    background: linear-gradient(135deg, #28a745, #00ff87);
    transform: translateY(-1px);
}

.quick-links {
    display: flex;
    flex-direction: column;
    gap: 8px;
    padding-top: 15px;
    border-top: 1px solid #e0e0e0;
}

.quick-link {
    padding: 8px 12px;
    color: #555;
    text-decoration: none;
    font-size: 0.85rem;
    border-radius: 6px;
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    gap: 8px;
}

.quick-link:hover {
    background: #f1f3f4;
    color: #37003c;
    transform: translateX(5px);
}

.quick-link.logout {
    color: #dc3545;
}

.quick-link.logout:hover {
    background: #ffeaea;
    color: #c82333;
}

/* Sign In Widget Specific Styles */
.sidebar-signin-widget {
    border: 2px solid #e8f5e8;
    transition: all 0.3s ease;
}

.sidebar-signin-widget:hover {
    box-shadow: 0 8px 25px rgba(40,167,69,0.15);
    transform: translateY(-2px);
}

.widget-header.signin {
    background: linear-gradient(135deg, #28a745, #20c997);
    color: white;
}

.signin-subtitle {
    font-size: 0.85rem;
    margin-top: 5px;
    opacity: 0.9;
}

.signin-benefits {
    display: grid;
    grid-template-columns: 1fr;
    gap: 15px;
    margin-bottom: 20px;
}

.benefit-item {
    display: flex;
    align-items: center;
    gap: 10px;
    padding: 12px;
    background: linear-gradient(135deg, #f8f9fa, #e9ecef);
    border-radius: 8px;
    border-left: 3px solid #28a745;
}

.benefit-icon {
    font-size: 1.2rem;
}

.benefit-info {
    display: flex;
    flex-direction: column;
}

.benefit-label {
    font-size: 0.85rem;
    color: #37003c;
    font-weight: 600;
}

.benefit-desc {
    font-size: 0.75rem;
    color: #666;
}

.signin-actions {
    display: flex;
    flex-direction: column;
    gap: 10px;
    margin-bottom: 15px;
}

.signin-btn {
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 12px 15px;
    border-radius: 8px;
    text-decoration: none;
    font-weight: 600;
    font-size: 0.9rem;
    transition: all 0.3s ease;
}

.signin-btn.primary {
    background: linear-gradient(135deg, #37003c, #6f42c1);
    color: white;
}

.signin-btn.primary:hover {
    background: linear-gradient(135deg, #6f42c1, #37003c);
    transform: translateY(-1px);
}

.signin-btn.secondary {
    background: linear-gradient(135deg, #28a745, #20c997);
    color: white;
}

.signin-btn.secondary:hover {
    background: linear-gradient(135deg, #20c997, #28a745);
    transform: translateY(-1px);
}

.signin-footer {
    text-align: center;
    padding-top: 15px;
    border-top: 1px solid #e0e0e0;
}

.signin-footer p {
    font-size: 0.8rem;
    color: #666;
    margin: 0;
}

/* Utility Classes */
.hidden {
    display: none !important;
}

.widget-header {
    background: linear-gradient(135deg, #37003c, #6f42c1);
    color: white;
    padding: 15px 20px;
    text-align: center;
}

.widget-header.premium {
    background: linear-gradient(135deg, #20c997, #17a2b8);
}

.widget-header h4 {
    margin: 0;
    font-size: 1.1rem;
    font-weight: 600;
}

.widget-content {
    padding: 20px;
}

.widget-content p {
    margin: 0 0 15px 0;
    color: #666;
    font-size: 0.9rem;
    line-height: 1.4;
}

