file.content:
.widget-form {
    display: flex;
    flex-direction: column;
    gap: 10px;
    margin-bottom: 15px;
}

.widget-form input {
    padding: 10px 12px;
    border: 2px solid #e9ecef;
    border-radius: 6px;
    font-size: 0.9rem;
}

.widget-form input:focus {
    outline: none;
    border-color: #37003c;
}

.widget-form button {
    background: #37003c;
    color: white;
    border: none;
    padding: 10px 15px;
    border-radius: 6px;
    font-weight: 600;
    cursor: pointer;
    transition: background 0.3s ease;
}

.widget-form button:hover {
    background: #6f42c1;
}

.widget-stats {
    display: flex;
    flex-direction: column;
    gap: 5px;
}

.widget-stats span {
    font-size: 0.8rem;
    color: #888;
}

.premium-highlights {
    display: flex;
    flex-direction: column;
    gap: 8px;
    margin-bottom: 15px;
}

.highlight {
    font-size: 0.9rem;
    color: #666;
    padding: 5px 0;
}

.widget-price {
    color: #20c997;
    font-weight: 700;
    font-size: 1.1rem;
    text-align: center;
    margin-bottom: 15px;
}

.widget-premium-btn {
    display: block;
    background: linear-gradient(135deg, #37003c, #6f42c1);
    color: white;
    text-decoration: none;
    text-align: center;
    padding: 12px;
    border-radius: 6px;
    font-weight: 600;
    transition: all 0.3s ease;
}

.widget-premium-btn:hover {
    background: linear-gradient(135deg, #6f42c1, #37003c);
    transform: translateY(-1px);
}

/* Auth Links Styling */
.nav-content {
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 40px;
}

.nav-auth-links {
    display: flex;
    list-style: none;
    margin: 0;
    padding: 0;
    gap: 15px;
}

.auth-link {
    text-decoration: none;
    padding: 8px 16px;
    border-radius: 20px;
    font-weight: 600;
    font-size: 0.9rem;
    transition: all 0.3s ease;
    border: 2px solid transparent;
}

.auth-link.signin {
    color: #37003c;
    border-color: #37003c;
}

.auth-link.signin:hover {
    background: #37003c;
    color: white;
}

.auth-link.create-account {
    background: linear-gradient(135deg, #37003c, #6f42c1);
    color: white;
}

.auth-link.create-account:hover {
    background: linear-gradient(135deg, #6f42c1, #37003c);
    transform: translateY(-1px);
    box-shadow: 0 4px 12px rgba(55,0,60,0.3);
}

/* Touch and Interaction Improvements */
@media (hover: none) and (pointer: coarse) {
    /* Touch devices */
    .story-card:hover,
    .story-card.compact:hover,
    .story-card.enhanced:hover {
        transform: none !important;
    }

    .headline-item:hover {
        transform: none !important;
    }

    .trending-item:hover {
        background: transparent !important;
        padding: 12px 5px !important;
    }

}

/* Improved Touch Targets */
.nav-links a,
.search-btn,
.menu-btn,
.newsletter-btn,
.newsletter-banner-btn {
    min-height: 44px;
    min-width: 44px;
    display: flex;
    align-items: center;
    justify-content: center;
}

/* Better Focus States for Accessibility */
input:focus,
button:focus,
a:focus {
    outline: 2px solid #0066cc;
    outline-offset: 2px;
}

/* Ensure Images are Responsive */
img {
    max-width: 100%;
    height: auto;
}

/* Prevent Horizontal Scroll */
* {
    box-sizing: border-box;
}

.container,
.content-wrapper,
.header-content,
.nav-content,
.footer {
    max-width: 100%;
    overflow-x: hidden;
}

/* Enhanced Homepage Styles */

/* Featured Story Section */
.featured-story {
    margin-bottom: 35px;
    animation: fadeIn 0.8s ease;
}

@keyframes fadeIn {

