/* Secondary Stories Compact Design */
.secondary-stories {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 24px;
    margin-top: 30px;
}

.story-card.compact {
    background: linear-gradient(145deg, #ffffff 0%, #f8f9fa 100%);
