    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.story-content-wrapper {
    padding: 20px;
}

.story-headline a {
    color: #333;
    text-decoration: none;
    font-size: 1.2rem;
    font-weight: 700;
    line-height: 1.3;
    transition: color 0.3s ease;
}

.story-headline a:hover {
    color: #37003c;
}

.story-summary {
    margin: 12px 0 15px 0;
    font-size: 0.95rem;
    line-height: 1.5;
    color: #666;
}

.story-footer {
    display: flex;
    justify-content: space-between;
    align-items: center;
    font-size: 0.85rem;
    padding-top: 12px;
    border-top: 1px solid rgba(0,0,0,0.05);
    margin-top: auto;
}

.story-date {
    color: #777;
    font-weight: 500;
}

.read-more {
    color: #37003c;
    font-weight: 600;
    transition: color 0.3s ease;
}

.story-card.compact:hover .read-more {
    color: #6f42c1;
}

/* Reduce spacing between sections */
.hero-section {
    margin-bottom: 20px;
}

.main-content {
    padding: 0;
}

.top-stories {
    margin-bottom: 20px;
}

.featured-stories {
    margin: 15px 0 20px 0;
    padding: 0 20px;
}


/* Mobile Responsiveness for Compact Cards */
@media (max-width: 768px) {
    .secondary-stories {
        grid-template-columns: 1fr;
        gap: 15px;
        margin-top: 20px;
    }

    .story-image-wrapper {
        height: 150px;
    }

    .story-content-wrapper {
        padding: 15px;
    }

    .story-headline a {
        font-size: 1.1rem;
    }

    .story-summary {
        font-size: 0.9rem;
    }
}

.featured-grid {
    display: grid;
    grid-template-columns: 2fr 1fr;
    gap: 30px;
    margin-top: 30px;
}

.featured-main {
    background: linear-gradient(145deg, #ffffff 0%, #f8f9fa 100%);
    border-radius: 16px;
    overflow: hidden;
    box-shadow: 0 8px 30px rgba(0,0,0,0.12);
    transition: all 0.3s ease;
    border: 1px solid rgba(0,0,0,0.05);
}

.featured-main:hover {
    transform: translateY(-5px);
    box-shadow: 0 15px 40px rgba(0,0,0,0.18);
}

.featured-image {
    position: relative;
    height: 300px;
    overflow: hidden;
}

.featured-image img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.3s ease;
}

.featured-main:hover .featured-image img {
    transform: scale(1.05);
}

.image-overlay {
    position: absolute;
    top: 20px;
    left: 20px;
    display: flex;
    gap: 10px;
}

.category-tag {
    background: linear-gradient(135deg, #37003c, #6f42c1);
    color: white;
    padding: 6px 12px;
    border-radius: 20px;
    font-size: 0.8rem;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.category-tag.transfers {
    background: linear-gradient(135deg, #28a745, #20c997);
}

.read-time {
    background: rgba(0,0,0,0.7);
    color: white;
    padding: 6px 12px;
    border-radius: 20px;
    font-size: 0.8rem;
    backdrop-filter: blur(10px);
}

.featured-content {
    padding: 30px;
}

.featured-title a {
    color: #333;
    text-decoration: none;
    font-size: 1.8rem;
    font-weight: 700;
    line-height: 1.3;
    transition: color 0.3s ease;
}

.featured-title a:hover {
    color: #37003c;
}

.featured-excerpt {
    margin: 15px 0 20px 0;
    font-size: 1.1rem;
    line-height: 1.6;
    color: #666;
}

.article-meta {
    display: flex;
    gap: 20px;
    flex-wrap: wrap;
    font-size: 0.9rem;
    color: #888;
}

.featured-secondary {
    display: flex;
    flex-direction: column;
    gap: 20px;
}

.story-card.enhanced {
    background: linear-gradient(145deg, #ffffff 0%, #f8f9fa 100%);
    border-radius: 12px;
    overflow: hidden;
    box-shadow: 0 4px 20px rgba(0,0,0,0.08);
    transition: all 0.3s ease;
    border: 1px solid rgba(0,0,0,0.05);
}

.story-card.enhanced:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 25px rgba(0,0,0,0.15);
}

.story-image {
    position: relative;
    height: 120px;

