  3406→        // Countdown Timer for FPL Banner
  3407→        function startCountdown() {
  3408→            const countdownEl = document.getElementById('countdown');
  3409→            if (!countdownEl) return;
  3410→            
  3411→            // Set end time to 24 hours from now
  3412→            let endTime = localStorage.getItem('fplOfferEnd');
  3413→            if (!endTime || new Date(endTime) < new Date()) {
  3414→                endTime = new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString();
  3415→                localStorage.setItem('fplOfferEnd', endTime);
  3416→            }
  3417→            
  3418→            function updateCountdown() {
  3419→                const now = new Date().getTime();
  3420→                const end = new Date(endTime).getTime();
  3421→                const distance = end - now;
  3422→                
  3423→                if (distance < 0) {
  3424→                    countdownEl.textContent = "EXPIRED";
  3425→                    clearInterval(countdownInterval);
  3426→                    // Reset for next visit
  3427→                    localStorage.removeItem('fplOfferEnd');
  3428→                    return;
  3429→                }
  3430→                
  3431→                const hours = Math.floor(distance / (1000 * 60 * 60));
  3432→                const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
  3433→                const seconds = Math.floor((distance % (1000 * 60)) / 1000);
  3434→                
  3435→                countdownEl.textContent = 
  3436→                    String(hours).padStart(2, '0') + ':' +
  3437→                    String(minutes).padStart(2, '0') + ':' +
  3438→                    String(seconds).padStart(2, '0');
  3439→            }
  3440→            
  3441→            updateCountdown();
  3442→            const countdownInterval = setInterval(updateCountdown, 1000);
  3443→        }
  3444→        
  3445→        // Start countdown on page load
  3446→        startCountdown();
  3447→        
  3448→        // FPL Promo Countdown Timer
  3449→        function updatePromoCountdown() {
  3450→            // Set the end date - 7 days from now or use saved date
  3451→            const promoEndDate = new Date();
  3452→            promoEndDate.setDate(promoEndDate.getDate() + 7);
  3453→            promoEndDate.setHours(23, 59, 59, 999);
  3454→            
  3455→            // Check if there's a saved end date in localStorage
  3456→            let savedEndDate = localStorage.getItem('fplPromoEndDate');
  3457→            if (!savedEndDate) {
  3458→                localStorage.setItem('fplPromoEndDate', promoEndDate.getTime());
  3459→                savedEndDate = promoEndDate.getTime();
  3460→            }
  3461→            
  3462→            const endDate = new Date(parseInt(savedEndDate));
  3463→            
  3464→            function updateTimer() {
  3465→                const now = new Date().getTime();
  3466→                const timeLeft = endDate - now;
  3467→                
  3468→                if (timeLeft <= 0) {
  3469→                    const elements = ['promo-days', 'promo-hours', 'promo-minutes', 'promo-seconds'];
  3470→                    elements.forEach(id => {
  3471→                        const el = document.getElementById(id);
  3472→                        if (el) el.textContent = '00';
  3473→                    });
  3474→                    return;
  3475→                }
  3476→                
  3477→                const days = Math.floor(timeLeft / (1000 * 60 * 60 * 24));
  3478→                const hours = Math.floor((timeLeft % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
  3479→                const minutes = Math.floor((timeLeft % (1000 * 60 * 60)) / (1000 * 60));
  3480→                const seconds = Math.floor((timeLeft % (1000 * 60)) / 1000);
  3481→                
  3482→                const daysEl = document.getElementById('promo-days');
  3483→                const hoursEl = document.getElementById('promo-hours');
  3484→                const minutesEl = document.getElementById('promo-minutes');
  3485→                const secondsEl = document.getElementById('promo-seconds');
  3486→                
  3487→                if (daysEl) daysEl.textContent = String(days).padStart(2, '0');
  3488→                if (hoursEl) hoursEl.textContent = String(hours).padStart(2, '0');
  3489→                if (minutesEl) minutesEl.textContent = String(minutes).padStart(2, '0');
  3490→                if (secondsEl) secondsEl.textContent = String(seconds).padStart(2, '0');
  3491→            }
  3492→            
  3493→            updateTimer();
  3494→            setInterval(updateTimer, 1000);
  3495→        }
  3496→        
  3497→        // Close FPL Promo Banner
  3498→        function closeFPLPromo() {
  3499→            const banner = document.getElementById('fpl-promo-banner');
  3500→            if (banner) {
  3501→                banner.style.transition = 'opacity 0.3s ease, transform 0.3s ease';
  3502→                banner.style.opacity = '0';
  3503→                banner.style.transform = 'translateY(-20px)';
  3504→                setTimeout(() => {
  3505→                    banner.classList.add('hidden');
  3506→                }, 300);
  3507→                localStorage.setItem('fplPromoClosed', 'true');
  3508→                localStorage.setItem('fplPromoClosedDate', new Date().getTime());
  3509→            }
  3510→        }
  3511→        
  3512→        // Make functions available globally
  3513→        window.closeFPLPromo = closeFPLPromo;
  3514→        window.closeFPLBanner = closeFPLPromo; // Keep backward compatibility
  3515→        
  3516→        // Check if banner should be shown
  3517→        function checkFPLBannerStatus() {
  3518→            const bannerClosed = localStorage.getItem('fplBannerClosed');
  3519→            const closedTime = localStorage.getItem('fplBannerClosedTime');
  3520→            
  3521→            if (bannerClosed && closedTime) {
  3522→                const dayInMs = 24 * 60 * 60 * 1000;
  3523→                const now = new Date().getTime();
  3524→                
  3525→                // If more than 24 hours have passed, show banner again
  3526→                if (now - parseInt(closedTime) > dayInMs) {
  3527→                    localStorage.removeItem('fplBannerClosed');
  3528→                    localStorage.removeItem('fplBannerClosedTime');
  3529→                } else {
  3530→                    // Hide banner if within 24 hours
  3531→                    const banner = document.getElementById('fpl-banner');
  3532→                    if (banner) {
  3533→                        banner.style.display = 'none';
  3534→                    }
  3535→                }
  3536→            }
  3537→        }
