    sessionStorage.setItem('premiumBannerClosed', 'true');
    // Redirect to homepage
    window.location.href = '/';
}

// Countdown Timer for FPL Banner
function startCountdown() {
    const countdownEl = document.getElementById('countdown');
    if (!countdownEl) return;

    // Set end time to 24 hours from now
    let endTime = localStorage.getItem('fplOfferEnd');
    if (!endTime || new Date(endTime) < new Date()) {
        endTime = new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString();
        localStorage.setItem('fplOfferEnd', endTime);
    }

    function updateCountdown() {
        const now = new Date().getTime();
        const end = new Date(endTime).getTime();
        const distance = end - now;

        if (distance < 0) {
            countdownEl.textContent = "EXPIRED";
            clearInterval(countdownInterval);
            // Reset for next visit
            localStorage.removeItem('fplOfferEnd');
            return;
        }

        const hours = Math.floor(distance / (1000 * 60 * 60));
        const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
        const seconds = Math.floor((distance % (1000 * 60)) / 1000);

        countdownEl.textContent = 
            String(hours).padStart(2, '0') + ':' +
            String(minutes).padStart(2, '0') + ':' +
            String(seconds).padStart(2, '0');
    }

    updateCountdown();
    const countdownInterval = setInterval(updateCountdown, 1000);
}

// Start countdown on page load
startCountdown();

// FPL Promo Countdown Timer
function updatePromoCountdown() {
    // Set the end date - 7 days from now or use saved date
    const promoEndDate = new Date();
    promoEndDate.setDate(promoEndDate.getDate() + 7);
    promoEndDate.setHours(23, 59, 59, 999);

    // Check if there's a saved end date in localStorage
    let savedEndDate = localStorage.getItem('fplPromoEndDate');
    if (!savedEndDate) {
        localStorage.setItem('fplPromoEndDate', promoEndDate.getTime());
        savedEndDate = promoEndDate.getTime();
    }

    const endDate = new Date(parseInt(savedEndDate));

    function updateTimer() {
        const now = new Date().getTime();
        const timeLeft = endDate - now;

        if (timeLeft <= 0) {
            const elements = ['promo-days', 'promo-hours', 'promo-minutes', 'promo-seconds'];
            elements.forEach(id => {
                const el = document.getElementById(id);
                if (el) el.textContent = '00';
            });
            return;
        }

        const days = Math.floor(timeLeft / (1000 * 60 * 60 * 24));
        const hours = Math.floor((timeLeft % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
        const minutes = Math.floor((timeLeft % (1000 * 60 * 60)) / (1000 * 60));
        const seconds = Math.floor((timeLeft % (1000 * 60)) / 1000);

        const daysEl = document.getElementById('promo-days');
        const hoursEl = document.getElementById('promo-hours');
        const minutesEl = document.getElementById('promo-minutes');
        const secondsEl = document.getElementById('promo-seconds');

        if (daysEl) daysEl.textContent = String(days).padStart(2, '0');
        if (hoursEl) hoursEl.textContent = String(hours).padStart(2, '0');
        if (minutesEl) minutesEl.textContent = String(minutes).padStart(2, '0');
        if (secondsEl) secondsEl.textContent = String(seconds).padStart(2, '0');
    }

    updateTimer();
    setInterval(updateTimer, 1000);
}

// Close FPL Promo Banner
function closeFPLPromo() {
    const banner = document.getElementById('fpl-promo-banner');
    if (banner) {
        banner.style.transition = 'opacity 0.3s ease, transform 0.3s ease';
        banner.style.opacity = '0';
        banner.style.transform = 'translateY(-20px)';
        setTimeout(() => {
            banner.classList.add('hidden');
        }, 300);
        localStorage.setItem('fplPromoClosed', 'true');
        localStorage.setItem('fplPromoClosedDate', new Date().getTime());
    }
}

// Make functions available globally
window.closeFPLPromo = closeFPLPromo;
window.closeFPLBanner = closeFPLPromo; // Keep backward compatibility

// Check if banner should be shown
function checkFPLBannerStatus() {
    const bannerClosed = localStorage.getItem('fplBannerClosed');
    const closedTime = localStorage.getItem('fplBannerClosedTime');

    if (bannerClosed && closedTime) {
        const dayInMs = 24 * 60 * 60 * 1000;
        const now = new Date().getTime();

        // If more than 24 hours have passed, show banner again
        if (now - parseInt(closedTime) > dayInMs) {
            localStorage.removeItem('fplBannerClosed');
            localStorage.removeItem('fplBannerClosedTime');
        } else {
            // Hide banner if within 24 hours
            const banner = document.getElementById('fpl-banner');
            if (banner) {
                banner.style.display = 'none';
            }
        }
    }
}

// Check banner status on page load
checkFPLBannerStatus();

// Newsletter signup functionality (disabled - using FPL banner now)
document.addEventListener('DOMContentLoaded', function() {
    // Remove unwanted ads first
    removeUnwantedAds();

    // Check if FPL promo banner was closed recently (within 24 hours)
    const closedDate = localStorage.getItem('fplPromoClosedDate');
    if (closedDate) {
        const daysSinceClosed = (new Date().getTime() - parseInt(closedDate)) / (1000 * 60 * 60 * 24);
        if (daysSinceClosed < 1) {
            const banner = document.getElementById('fpl-promo-banner');
            if (banner) {
                banner.classList.add('hidden');
            }
        } else {
            // Reset if more than 24 hours
            localStorage.removeItem('fplPromoClosed');
            localStorage.removeItem('fplPromoClosedDate');
            updatePromoCountdown();
        }
    } else {
        // Start the countdown if banner is visible
        updatePromoCountdown();
    }

    // Check if premium banner should be shown
    checkPremiumBannerVisibility();

    // Check account status and show account widget if signed in
    checkAccountStatus();

    // Check header authentication state
    checkHeaderAuthState();

    // Track article view if user is signed in
    if (localStorage.getItem('user')) {
        incrementArticlesRead();
    }

    // Banner now shows on every page load - localStorage check removed

    // Banner form handler
    const bannerForm = document.getElementById('newsletter-banner-form');
    const bannerMessageDiv = document.getElementById('newsletter-banner-message');
    const bannerSubmitBtn = bannerForm.querySelector('.newsletter-banner-btn');
    const bannerBtnText = bannerSubmitBtn.querySelector('.banner-btn-text');
    const bannerBtnLoader = bannerSubmitBtn.querySelector('.banner-btn-loader');

    bannerForm.addEventListener('submit', async function(e) {
        e.preventDefault();
        await handleNewsletterSubmit(
            'banner-newsletter-name',
            'banner-newsletter-email',
            bannerForm,
            bannerMessageDiv,
            bannerSubmitBtn,
            bannerBtnText,
            bannerBtnLoader
        );
    });

    // Sidebar form handler (if it exists)
    const newsletterForm = document.getElementById('newsletter-form');
    if (newsletterForm) {
        const messageDiv = document.getElementById('newsletter-message');
        const submitBtn = newsletterForm.querySelector('.newsletter-btn');
        const btnText = submitBtn.querySelector('.btn-text');
        const btnLoader = submitBtn.querySelector('.btn-loader');

        newsletterForm.addEventListener('submit', async function(e) {
            e.preventDefault();
            await handleNewsletterSubmit(
                'newsletter-name',
                'newsletter-email',
                newsletterForm,
                messageDiv,
                submitBtn,
                btnText,
                btnLoader
            );
        });
    }

    // Shared newsletter submission handler
    async function handleNewsletterSubmit(nameId, emailId, form, messageDiv, submitBtn, btnText, btnLoader) {
        const name = document.getElementById(nameId).value.trim();
        const email = document.getElementById(emailId).value.trim();

        // Basic validation
        if (!name || !email) {
            showMessage(messageDiv, 'Please fill in all fields.', 'error');
            return;
        }

        if (!isValidEmail(email)) {
            showMessage(messageDiv, 'Please enter a valid email address.', 'error');
            return;
        }

        // Check if email is unsubscribed
        const unsubscribed = JSON.parse(localStorage.getItem('unsubscribed-emails') || '[]');
        if (unsubscribed.includes(email.toLowerCase())) {
            showMessage(messageDiv, '⚠️ This email was previously unsubscribed. Please use our <a href="manage-subscription.html?email=' + encodeURIComponent(email) + '" target="_blank">Manage Subscription</a> page to resubscribe.', 'error');
            return;
        }

        // Show loading state
        submitBtn.disabled = true;
        btnText.style.display = 'none';
        btnLoader.style.display = 'inline';

        try {
