                    // Auto-hide banner after successful subscription
                    if (form.id === 'newsletter-banner-form') {
                        setTimeout(() => {
                            closeBanner();
                        }, 3000);
                    }
                }, 1000);
            } else {
                throw new Error('Subscription failed');
