  1617→
  1618→<script>
  1619→    // Enhanced Search Functionality
  1620→    let searchTimeout;
  1621→    let isSearching = false;
  1622→    let searchCache = new Map();
  1623→    
  1624→    // Initialize search functionality
  1625→    document.addEventListener('DOMContentLoaded', function() {
  1626→        initializeSearch();
  1627→    });
  1628→    
  1629→    function initializeSearch() {
  1630→        const searchInput = document.getElementById('search-input');
  1631→        const searchBtn = document.querySelector('.search-btn');
  1632→        const searchClear = document.querySelector('.search-clear');
  1633→        
  1634→        if (searchInput) {
  1635→            // Add event listeners
  1636→            searchInput.addEventListener('input', handleSearchInput);
  1637→            searchInput.addEventListener('keydown', handleSearchKeydown);
  1638→            searchInput.addEventListener('focus', handleSearchFocus);
  1639→            searchInput.addEventListener('blur', handleSearchBlur);
  1640→        }
  1641→        
  1642→        if (searchClear) {
  1643→            searchClear.addEventListener('click', clearSearch);
  1644→        }
  1645→        
  1646→        // Close dropdown when clicking outside
  1647→        document.addEventListener('click', function(event) {
  1648→            const searchContainer = document.querySelector('.search-container');
  1649→            if (searchContainer && !searchContainer.contains(event.target)) {
  1650→                hideSearchResults();
  1651→            }
  1652→        });
  1653→    }
  1654→    
  1655→    function handleSearchInput(event) {
  1656→        const query = event.target.value;
  1657→        const searchClear = document.querySelector('.search-clear');
  1658→        
  1659→        // Show/hide clear button
  1660→        if (searchClear) {
  1661→            searchClear.style.display = query.length > 0 ? 'flex' : 'none';
  1662→        }
  1663→        
  1664→        // Debounced search
  1665→        clearTimeout(searchTimeout);
  1666→        if (query.length >= 2) {
  1667→            searchTimeout = setTimeout(() => {
  1668→                performInstantSearch(query);
  1669→            }, 300);
  1670→        } else {
  1671→            hideSearchResults();
  1672→        }
  1673→    }
  1674→    
  1675→    function handleSearchKeydown(event) {
  1676→        if (event.key === 'Enter') {
  1677→            event.preventDefault();
  1678→            const query = event.target.value.trim();
  1679→            if (query) {
  1680→                performSearch(query);
  1681→            }
  1682→        } else if (event.key === 'Escape') {
  1683→            hideSearchResults();
  1684→            event.target.blur();
  1685→        }
  1686→    }
  1687→    
  1688→    function handleSearchFocus(event) {
  1689→        const query = event.target.value;
  1690→        if (query.length >= 2) {
  1691→            performInstantSearch(query);
  1692→        }
  1693→    }
  1694→    
  1695→    function handleSearchBlur(event) {
  1696→        // Delay hiding to allow click events on results
  1697→        setTimeout(() => {
  1698→            if (!document.querySelector('.search-results-dropdown:hover')) {
  1699→                hideSearchResults();
  1700→            }
  1701→        }, 200);
  1702→    }
  1703→    
  1704→    function clearSearch() {
  1705→        const searchInput = document.getElementById('search-input');
  1706→        const searchClear = document.querySelector('.search-clear');
  1707→        
  1708→        if (searchInput) {
  1709→            searchInput.value = '';
  1710→            searchInput.focus();
  1711→        }
  1712→        if (searchClear) {
  1713→            searchClear.style.display = 'none';
  1714→        }
  1715→        hideSearchResults();
  1716→    }
  1717→    
  1718→    function performInstantSearch(query) {
  1719→        if (isSearching) return;
  1720→        
  1721→        // Check cache first
  1722→        if (searchCache.has(query)) {
  1723→            displaySearchResults(searchCache.get(query), query);
  1724→            return;
  1725→        }
  1726→        
  1727→        isSearching = true;
  1728→        showSearchLoading();
  1729→        
  1730→        // Simulate API call delay
  1731→        setTimeout(() => {
  1732→            const results = searchArticles(query);
  1733→            searchCache.set(query, results);
  1734→            displaySearchResults(results, query);
  1735→            isSearching = false;
  1736→        }, 150);
  1737→    }
  1738→    
  1739→    function performSearch(query) {
  1740→        if (!query) {
  1741→            query = document.getElementById('search-input').value.trim();
  1742→        }
  1743→        
  1744→        if (!query) return;
  1745→        
  1746→        // For now, just show instant results
  1747→        // In a real app, this would navigate to a full search results page
  1748→        performInstantSearch(query);
  1749→        
  1750→        // Optional: Navigate to full search page
  1751→        // window.location.href = `/search.html?q=${encodeURIComponent(query)}`;
  1752→    }
  1753→    
  1754→    function searchArticles(query) {
  1755→        // Article database (in real app, this would be fetched from an API)
  1756→        const articles = [
  1757→            {
  1758→                title: "Arsenal Transfer News: Latest Updates and Rumors",
  1759→                snippet: "Arsenal are reportedly interested in signing a new striker this summer transfer window...",
  1760→                url: "/articles/arsenal-transfer-news-latest-updates.html",
  1761→                date: "2025-01-12",
  1762→                category: "Transfer News"
  1763→            },
  1764→            {
  1765→                title: "Manchester United vs Liverpool: Match Preview",
  1766→                snippet: "The biggest rivalry in English football returns as United host Liverpool at Old Trafford...",
  1767→                url: "/articles/man-united-vs-liverpool-preview.html",
  1768→                date: "2025-01-11",
  1769→                category: "Match Preview"
  1770→            },
  1771→            {
  1772→                title: "FPL Tips: Best Captain Picks for Gameweek 21",
  1773→                snippet: "Fantasy Premier League managers are looking for the best captain options this gameweek...",
  1774→                url: "/articles/fpl-tips-gameweek-21-captain-picks.html",
  1775→                date: "2025-01-10",
  1776→                category: "FPL Tips"
  1777→            },
  1778→            {
  1779→                title: "Premier League Table: Who's in the Title Race?",
  1780→                snippet: "After 20 gameweeks, the Premier League title race is heating up with several contenders...",
  1781→                url: "/articles/premier-league-title-race-analysis.html",
  1782→                date: "2025-01-09",
  1783→                category: "Analysis"
  1784→            },
  1785→            {
  1786→                title: "Chelsea's New Manager: Impact and Expectations",
  1787→                snippet: "Chelsea have appointed a new manager and fans are wondering what changes to expect...",
  1788→                url: "/articles/chelsea-new-manager-analysis.html",
  1789→                date: "2025-01-08",
  1790→                category: "News"
  1791→            }
  1792→        ];
  1793→        
  1794→        const queryLower = query.toLowerCase();
  1795→        return articles.filter(article => 
  1796→            article.title.toLowerCase().includes(queryLower) ||
  1797→            article.snippet.toLowerCase().includes(queryLower) ||
  1798→            article.category.toLowerCase().includes(queryLower)
  1799→        );
  1800→    }
  1801→    
  1802→    function showSearchLoading() {
  1803→        const dropdown = document.getElementById('search-results');
  1804→        if (!dropdown) return;
  1805→        
  1806→        dropdown.innerHTML = `
  1807→            <div class="search-loading">
  1808→                <div class="search-spinner"></div>
  1809→                Searching articles...
  1810→            </div>
  1811→        `;
  1812→        dropdown.classList.add('active');
  1813→    }
  1814→    
  1815→    function displaySearchResults(results, query) {
  1816→        const dropdown = document.getElementById('search-results');
  1817→        if (!dropdown) return;
  1818→        
  1819→        if (results.length === 0) {
  1820→            dropdown.innerHTML = `
  1821→                <div class="search-no-results">
  1822→                    <div class="search-no-results-icon">🔍</div>
  1823→                    <div>No articles found for "${query}"</div>
  1824→                    <div style="margin-top: 8px; font-size: 0.8rem; color: #999;">
  1825→                        Try different keywords or browse our latest articles
  1826→                    </div>
  1827→                </div>
  1828→            `;
  1829→        } else {
  1830→            const resultsHtml = `
  1831→                <div class="search-results-header">
  1832→                    <span>Search Results</span>
  1833→                    <span class="search-results-count">${results.length} article${results.length !== 1 ? 's' : ''}</span>
  1834→                </div>
  1835→                ${results.map(result => `
  1836→                    <div class="search-result-item" onclick="navigateToArticle('${result.url}')">
