   355→    // Insert ads into sidebar
   356→    function insertSidebarAds() {
   357→        // Check if we're on an article page or main page
   358→        const isArticlePage = window.location.pathname.includes('/articles/');
   359→        const sidebar = document.querySelector('.sidebar, .article-sidebar, aside');
