   361→        if (sidebar) {
   362→            // Select 2 random ads to show
   363→            const selectedAds = fplAds.sort(() => 0.5 - Math.random()).slice(0, 2);
   364→            const adsHTML = selectedAds.map(ad => createSidebarAd(ad)).join('');
   365→            
   366→            // Create ads container
   367→            const adsContainer = document.createElement('div');
   368→            adsContainer.className = 'fpl-ads-container';
   369→            adsContainer.innerHTML = adsHTML;
   370→            
   371→            // Insert at the top of sidebar
   372→            sidebar.insertBefore(adsContainer, sidebar.firstChild);
   373→        }
   374→    }
   375→
   376→    // Insert banner ad in articles
   377→    function insertBannerAd() {
   378→        const articleContent = document.querySelector('.article-content, .main-content article, article');
   379→        if (articleContent) {
   380→            const paragraphs = articleContent.querySelectorAll('p');
   381→            if (paragraphs.length > 3) {
   382→                // Insert after 3rd paragraph
   383→                const bannerDiv = document.createElement('div');
   384→                bannerDiv.innerHTML = createBannerAd();
   385→                paragraphs[2].after(bannerDiv);
   386→            }
   387→        }
   388→    }
   389→
   390→    // Insert floating ad
   391→    function insertFloatingAd() {
   392→        // Only show on certain pages
   393→        if (!window.location.pathname.includes('fpl-premium-hub')) {
   394→            const floatingDiv = document.createElement('div');
   395→            floatingDiv.innerHTML = createFloatingAd();
   396→            document.body.appendChild(floatingDiv.firstElementChild);
   397→            
   398→            // Add click handler
   399→            const floatingAd = document.getElementById('fpl-floating-ad');
   400→            if (floatingAd) {
   401→                floatingAd.addEventListener('click', function(e) {
   402→                    if (!e.target.classList.contains('floating-close')) {
   403→                        window.location.href = '/fpl-ai-assistant.html';
   404→                    }
   405→                });
   406→            }
   407→        }
   408→    }
   409→
   410→    // Track ad impressions and clicks
   411→    function trackAdEvent(eventType, adId) {
   412→        if (typeof gtag !== 'undefined') {
   413→            gtag('event', eventType, {
   414→                'event_category': 'FPL_Ads',
   415→                'event_label': adId
   416→            });
   417→        }
   418→    }
   419→
   420→    // Rotate ads periodically
   421→    function rotateAds() {
   422→        setInterval(() => {
   423→            const adsContainer = document.querySelector('.fpl-ads-container');
   424→            if (adsContainer) {
   425→                const selectedAds = fplAds.sort(() => 0.5 - Math.random()).slice(0, 2);
   426→                adsContainer.innerHTML = selectedAds.map(ad => createSidebarAd(ad)).join('');
   427→                attachAdListeners();
   428→            }
   429→        }, 30000); // Rotate every 30 seconds
   430→    }
   431→
   432→    // Attach click listeners to ads
   433→    function attachAdListeners() {
   434→        document.querySelectorAll('.fpl-ad-cta').forEach(cta => {
   435→            cta.addEventListener('click', function(e) {
   436→                const adId = this.closest('.fpl-sidebar-ad')?.dataset.adId;
   437→                if (adId) {
   438→                    trackAdEvent('click', adId);
   439→                }
   440→            });
   441→        });
   442→    }
   443→
   444→    // Initialize ads system
   445→    function init() {
   446→        // Don't show ads on FPL tool pages themselves
   447→        const fplPages = ['/fpl-ai-assistant.html', '/transfer-simulator-pro.html', '/player-predictor.html'];
   448→        const currentPath = window.location.pathname;
   449→        
   450→        if (!fplPages.some(page => currentPath.includes(page))) {
   451→            injectStyles();
   452→            insertSidebarAds();
   453→            insertBannerAd();
   454→            insertFloatingAd();
   455→            attachAdListeners();
   456→            rotateAds();
   457→        }
   458→    }
   459→
   460→    // Wait for DOM to be ready
   461→    if (document.readyState === 'loading') {
   462→        document.addEventListener('DOMContentLoaded', init);
   463→    } else {
   464→        init();
   465→    }
   466→})();

