                    position: fixed;
                    top: 0;
                    left: 0;
                    width: 100%;
                    height: 100%;
                    z-index: 9999;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    animation: fadeIn 0.3s ease;
                }

                @keyframes fadeIn {
                    from { opacity: 0; }
                    to { opacity: 1; }
                }

                .fpl-big-banner-popup.closing {
                    animation: fadeOut 0.3s ease;
                }

                @keyframes fadeOut {
                    from { opacity: 1; }
                    to { opacity: 0; }
                }

                .popup-overlay {
                    position: absolute;
                    top: 0;
                    left: 0;
                    width: 100%;
                    height: 100%;
                    background: rgba(0, 0, 0, 0.7);
                    backdrop-filter: blur(5px);
                }

                .popup-content {
                    position: relative;
                    max-width: 600px;
                    width: 90%;
                    max-height: 90vh;
                    overflow-y: auto;
                    border-radius: 20px;
                    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
                    animation: slideUp 0.4s ease;
                }

                @keyframes slideUp {
                    from { transform: translateY(50px); opacity: 0; }
                    to { transform: translateY(0); opacity: 1; }
                }

                .popup-close {
                    position: absolute;
                    top: 15px;
                    right: 15px;
                    background: rgba(255, 255, 255, 0.2);
                    border: none;
                    color: white;
                    font-size: 30px;
                    width: 40px;
                    height: 40px;
                    border-radius: 50%;
                    cursor: pointer;
                    transition: all 0.3s ease;
                    z-index: 10;
                }

                .popup-close:hover {
                    background: rgba(255, 255, 255, 0.3);
                    transform: rotate(90deg);
                }

                .popup-inner {
                    padding: 40px;
                    color: white;
                    text-align: center;
                }

                .popup-icon {
                    font-size: 4rem;
                    margin-bottom: 20px;
                    animation: bounce 2s infinite;
                }

                @keyframes bounce {
                    0%, 100% { transform: translateY(0); }
                    50% { transform: translateY(-10px); }
                }

                .popup-title {
                    font-size: 2.5rem;
                    font-weight: 800;
                    margin-bottom: 10px;
                    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.2);
                }

                .popup-subtitle {
                    font-size: 1.2rem;
                    opacity: 0.95;
                    margin-bottom: 20px;
                }

                .popup-description {
                    font-size: 1.1rem;
                    line-height: 1.6;
                    margin-bottom: 25px;
                    opacity: 0.9;
                }

                .popup-features {
                    display: flex;
                    flex-wrap: wrap;
                    gap: 15px;
                    justify-content: center;
                    margin-bottom: 30px;
                }

                .feature-item {
                    background: rgba(255, 255, 255, 0.1);
                    padding: 8px 15px;
                    border-radius: 20px;
                    backdrop-filter: blur(10px);
                    font-size: 0.95rem;
                }

                .popup-cta-group {
                    display: flex;
                    gap: 15px;
                    justify-content: center;
                    flex-wrap: wrap;
                }

                .popup-cta-primary {
                    background: rgba(255, 255, 255, 0.95);
                    color: #38003c;
                    padding: 15px 35px;
                    border-radius: 30px;
                    text-decoration: none;
                    font-weight: 800;
                    font-size: 1.1rem;
                    transition: all 0.3s ease;
                    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
                }

                .popup-cta-primary:hover {
                    background: white;
                    transform: scale(1.05);
                    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.3);
                }

                .popup-cta-secondary {
                    background: transparent;
                    color: white;
                    padding: 15px 25px;
                    border: 2px solid rgba(255, 255, 255, 0.3);
                    border-radius: 30px;
                    font-weight: 600;
                    cursor: pointer;
                    transition: all 0.3s ease;
                }

                .popup-cta-secondary:hover {
                    background: rgba(255, 255, 255, 0.1);
                    border-color: rgba(255, 255, 255, 0.5);
                }

                .popup-special-badge {
                    position: absolute;
                    top: 20px;
                    left: 20px;
                    background: linear-gradient(135deg, #ff3366, #ff6633);
                    padding: 8px 20px;
                    border-radius: 20px;
                    font-size: 0.85rem;
                    font-weight: 700;
                    animation: pulse 2s infinite;
                }

                /* Banner Ads */
                .fpl-banner-ad {
                    border-radius: 12px;
                    padding: 25px;
                    margin: 30px 0;
                    color: white;
                    box-shadow: 0 5px 20px rgba(0,0,0,0.2);
                }

                .banner-content {
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                    flex-wrap: wrap;
                    gap: 20px;
                }

                .banner-left h3 {
                    font-size: 1.5rem;
                    margin-bottom: 5px;
                }

                .banner-left p {
                    opacity: 0.95;
                }

                .banner-cta {
                    background: rgba(255,255,255,0.2);
                    color: white;
                    padding: 12px 25px;
                    border-radius: 8px;
                    text-decoration: none;
                    font-weight: 700;
                    backdrop-filter: blur(10px);
                    transition: all 0.3s ease;
                    display: inline-block;
                }

                .banner-cta:hover {
                    background: rgba(255,255,255,0.3);
                    transform: scale(1.05);
                }

                /* Responsive */
                @media (max-width: 768px) {
                    .fpl-floating-ad {
                        bottom: 10px;
                        right: 10px;
                        padding: 10px 15px;
                    }

                    .floating-text {
                        display: none;
                    }

                    .banner-content {
                        flex-direction: column;
                        text-align: center;
                    }

                    .fpl-sidebar-ad {
                        margin: 10px;
                    }
                }
            </style>
        `;
        document.head.insertAdjacentHTML('beforeend', styles);
    }

    // Insert ads into sidebar
    function insertSidebarAds() {
        // Check if we're on an article page or main page
        const isArticlePage = window.location.pathname.includes('/articles/');
        const sidebar = document.querySelector('.sidebar, .article-sidebar, aside');

        if (sidebar) {
            // Select 2 random ads to show
            const selectedAds = fplAds.sort(() => 0.5 - Math.random()).slice(0, 2);
            const adsHTML = selectedAds.map(ad => createSidebarAd(ad)).join('');

            // Create ads container
            const adsContainer = document.createElement('div');
            adsContainer.className = 'fpl-ads-container';
            adsContainer.innerHTML = adsHTML;

            // Insert at the top of sidebar
            sidebar.insertBefore(adsContainer, sidebar.firstChild);
        }
    }

    // Insert banner ad in articles
    function insertBannerAd() {
        const articleContent = document.querySelector('.article-content, .main-content article, article');
        if (articleContent) {
            const paragraphs = articleContent.querySelectorAll('p');
            if (paragraphs.length > 3) {
                // Insert after 3rd paragraph
                const bannerDiv = document.createElement('div');
                bannerDiv.innerHTML = createBannerAd();
                paragraphs[2].after(bannerDiv);
            }
        }
    }

    // Insert floating ad
    function insertFloatingAd() {
        // Only show on certain pages
        if (!window.location.pathname.includes('fpl-premium-hub')) {
            const floatingDiv = document.createElement('div');
            floatingDiv.innerHTML = createFloatingAd();
            document.body.appendChild(floatingDiv.firstElementChild);

            // Add click handler
            const floatingAd = document.getElementById('fpl-floating-ad');
            if (floatingAd) {
                floatingAd.addEventListener('click', function(e) {
                    if (!e.target.classList.contains('floating-close')) {
                        window.location.href = '/fpl-ai-assistant.html';
                    }
                });
            }
        }
    }

    // Track ad impressions and clicks
    function trackAdEvent(eventType, adId) {
        if (typeof gtag !== 'undefined') {
            gtag('event', eventType, {
                'event_category': 'FPL_Ads',
                'event_label': adId
            });
        }
    }

    // Rotate ads periodically
    function rotateAds() {
        setInterval(() => {
            const adsContainer = document.querySelector('.fpl-ads-container');
            if (adsContainer) {
                const selectedAds = fplAds.sort(() => 0.5 - Math.random()).slice(0, 2);
                adsContainer.innerHTML = selectedAds.map(ad => createSidebarAd(ad)).join('');
                attachAdListeners();
            }
        }, 30000); // Rotate every 30 seconds
    }

    // Attach click listeners to ads
    function attachAdListeners() {
        document.querySelectorAll('.fpl-ad-cta').forEach(cta => {
            cta.addEventListener('click', function(e) {
                const adId = this.closest('.fpl-sidebar-ad')?.dataset.adId;
                if (adId) {
                    trackAdEvent('click', adId);
                }
            });
        });
    }

    // Initialize ads system
    function init() {
        // Don't show ads on FPL tool pages themselves
        const fplPages = ['/fpl-ai-assistant.html', '/transfer-simulator-pro.html', '/player-predictor.html'];
        const currentPath = window.location.pathname;

        if (!fplPages.some(page => currentPath.includes(page))) {
            injectStyles();
            insertSidebarAds();
            insertBannerAd();
            insertFloatingAd();
            attachAdListeners();
            rotateAds();
        }
    }

    // Wait for DOM to be ready
    if (document.readyState === 'loading') {
        document.addEventListener('DOMContentLoaded', init);
    } else {
        init();
    }
})();

