// FPL Premium Tools Advertisement System
(function() {
    'use strict';

    // Ad configurations with rotating content
    const fplAds = [
        {
            id: 'ai-assistant-ad',
            title: '🤖 FPL AI Assistant',
            subtitle: 'Your Personal FPL Expert',
            description: 'Get instant transfer advice, captain picks, and differential recommendations',
            cta: 'Try AI Assistant FREE',
            link: '/fpl-ai-assistant.html',
            color: 'linear-gradient(135deg, #667eea, #764ba2)',
            features: ['Spell-check aware', 'Remembers your team', 'xG Analysis'],
            priority: 1
        },
        {
            id: 'transfer-simulator-ad',
            title: '🔄 Transfer Simulator Pro',
            subtitle: 'Plan Perfect Transfers',
            description: 'AI-powered squad optimization with price predictions',
            cta: 'Optimize Squad Now',
            link: '/transfer-simulator-pro.html',
            color: 'linear-gradient(135deg, #f093fb, #f5576c)',
            features: ['AI Optimization', 'Price Tracking', 'What-if Analysis'],
            priority: 2
        },
        {
            id: 'predictor-ad',
            title: '🔮 Points Predictor',
            subtitle: 'ML-Powered Predictions',
            description: 'Know who will score big before the gameweek starts',
            cta: 'Predict Points',
            link: '/player-predictor.html',
            color: 'linear-gradient(135deg, #4facfe, #00f2fe)',
            features: ['5 GWs ahead', 'Captain picks', '85% accuracy'],
            priority: 3
        },
        {
            id: 'premium-bundle-ad',
            title: '💎 Premium Bundle',
            subtitle: 'Save 50% Today',
            description: 'Get all premium tools for the price of one',
            cta: 'Unlock Everything',
            link: '/fpl-premium-hub.html',
            color: 'linear-gradient(135deg, #fa709a, #fee140)',
            features: ['All Tools Included', 'Priority Support', 'Season Pass'],
            priority: 1,
            special: true
        }
    ];

    // Create sidebar ad HTML
    function createSidebarAd(ad) {
        return `
            <div class="fpl-sidebar-ad ${ad.special ? 'special-offer' : ''}" data-ad-id="${ad.id}">
                <div class="fpl-ad-header" style="background: ${ad.color}">
                    <h3 class="fpl-ad-title">${ad.title}</h3>
                    <p class="fpl-ad-subtitle">${ad.subtitle}</p>
                </div>
                <div class="fpl-ad-body">
                    <p class="fpl-ad-description">${ad.description}</p>
                    <ul class="fpl-ad-features">
                        ${ad.features.map(f => `<li>✓ ${f}</li>`).join('')}
                    </ul>
                    <a href="${ad.link}" class="fpl-ad-cta">
                        ${ad.cta}
                        <span class="cta-arrow">→</span>
                    </a>
                </div>
                ${ad.special ? '<div class="special-badge">LIMITED OFFER</div>' : ''}
            </div>
        `;
    }

    // Create floating corner ad
    function createFloatingAd() {
        return `
            <div class="fpl-floating-ad" id="fpl-floating-ad">
                <button class="floating-close" onclick="this.parentElement.style.display='none'">×</button>
                <div class="floating-content">
                    <div class="floating-icon">🤖</div>
                    <div class="floating-text">
                        <strong>Need FPL Help?</strong>
                        <span>AI Assistant is online!</span>
                    </div>
                </div>
            </div>
        `;
    }

    // Create big popup banner
    function createBigBannerPopup() {
        const randomAd = fplAds.filter(ad => ad.priority === 1)[0] || fplAds[0];
        return `
            <div class="fpl-big-banner-popup" id="fpl-big-banner-popup">
                <div class="popup-overlay"></div>
                <div class="popup-content" style="background: ${randomAd.color}">
                    <button class="popup-close" onclick="closeBigBanner()">×</button>
                    <div class="popup-inner">
                        <div class="popup-icon">🚀</div>
                        <h2 class="popup-title">${randomAd.title}</h2>
                        <p class="popup-subtitle">${randomAd.subtitle}</p>
                        <div class="popup-description">
                            <p>${randomAd.description}</p>
                        </div>
                        <div class="popup-features">
                            ${randomAd.features.map(f => `<div class="feature-item">✨ ${f}</div>`).join('')}
                        </div>
                        <div class="popup-cta-group">
                            <a href="${randomAd.link}" class="popup-cta-primary">${randomAd.cta}</a>
                            <button class="popup-cta-secondary" onclick="closeBigBanner()">Maybe Later</button>
                        </div>
                        ${randomAd.special ? '<div class="popup-special-badge">🔥 LIMITED TIME OFFER</div>' : ''}
                    </div>
                </div>
            </div>
        `;
    }

    // Close big banner function
    window.closeBigBanner = function() {
        const popup = document.getElementById('fpl-big-banner-popup');
        if (popup) {
            popup.classList.add('closing');
            setTimeout(() => {
                popup.remove();
            }, 300);
            // Set cookie to not show again for 24 hours
            document.cookie = "fplBannerClosed=true; max-age=86400; path=/";
        }
    };

    // Create banner ad for article pages
    function createBannerAd() {
        const randomAd = fplAds[Math.floor(Math.random() * fplAds.length)];
        return `
            <div class="fpl-banner-ad" style="background: ${randomAd.color}">
                <div class="banner-content">
                    <div class="banner-left">
                        <h3>${randomAd.title}</h3>
                        <p>${randomAd.description}</p>
                    </div>
                    <div class="banner-right">
                        <a href="${randomAd.link}" class="banner-cta">${randomAd.cta}</a>
                    </div>
                </div>
            </div>
        `;
    }

    // Add CSS styles
    function injectStyles() {
        const styles = `
            <style>
                /* Sidebar Ads */
                .fpl-sidebar-ad {
                    background: white;
                    border-radius: 12px;
                    overflow: hidden;
                    box-shadow: 0 5px 15px rgba(0,0,0,0.1);
                    margin-bottom: 20px;
                    transition: transform 0.3s ease;
                    position: relative;
                }

                .fpl-sidebar-ad:hover {
                    transform: translateY(-5px);
                    box-shadow: 0 10px 25px rgba(0,0,0,0.15);
                }

                .fpl-ad-header {
                    padding: 20px;
                    color: white;
                    text-align: center;
                }

                .fpl-ad-title {
                    font-size: 1.4rem;
                    font-weight: 800;
                    margin-bottom: 5px;
                }

                .fpl-ad-subtitle {
                    font-size: 0.9rem;
                    opacity: 0.95;
                }

                .fpl-ad-body {
                    padding: 20px;
                }

                .fpl-ad-description {
                    color: #666;
                    margin-bottom: 15px;
                    line-height: 1.5;
                }

                .fpl-ad-features {
                    list-style: none;
                    padding: 0;
                    margin-bottom: 20px;
                }

                .fpl-ad-features li {
                    padding: 5px 0;
                    color: #444;
                    font-size: 0.9rem;
                }

                .fpl-ad-cta {
                    display: block;
                    width: 100%;
                    padding: 12px 20px;
                    background: linear-gradient(135deg, #38003c, #4a0e4e);
                    color: white;
                    text-decoration: none;
                    border-radius: 8px;
                    font-weight: 700;
                    text-align: center;
                    transition: all 0.3s ease;
                    position: relative;
                }

                .fpl-ad-cta:hover {
                    background: linear-gradient(135deg, #4a0e4e, #38003c);
                    padding-right: 35px;
                }

                .cta-arrow {
                    position: absolute;
                    right: 20px;
                    top: 50%;
                    transform: translateY(-50%);
                    transition: right 0.3s ease;
                }

                .fpl-ad-cta:hover .cta-arrow {
                    right: 15px;
                }

                .special-offer {
                    border: 2px solid #ffd700;
                }

                .special-badge {
                    position: absolute;
                    top: 10px;
                    right: -35px;
                    background: linear-gradient(135deg, #ff3366, #ff6633);
                    color: white;
                    padding: 5px 40px;
                    transform: rotate(45deg);
                    font-size: 0.7rem;
                    font-weight: 700;
                    box-shadow: 0 3px 10px rgba(0,0,0,0.2);
                }

                /* Floating Corner Ad */
                .fpl-floating-ad {
                    position: fixed;
                    bottom: 20px;
                    right: 20px;
                    background: linear-gradient(135deg, #38003c, #00ff87);
                    border-radius: 60px;
                    padding: 15px 20px;
                    box-shadow: 0 10px 30px rgba(0,0,0,0.3);
                    z-index: 1000;
                    cursor: pointer;
                    transition: all 0.3s ease;
                    animation: pulse 2s infinite;
                }

                @keyframes pulse {
                    0%, 100% { transform: scale(1); }
                    50% { transform: scale(1.05); }
                }

                .fpl-floating-ad:hover {
                    transform: scale(1.1);
                    box-shadow: 0 15px 40px rgba(0,0,0,0.4);
                }

                .floating-close {
                    position: absolute;
                    top: -5px;
                    right: 5px;
                    background: rgba(255,255,255,0.9);
                    border: none;
                    border-radius: 50%;
                    width: 25px;
                    height: 25px;
                    font-size: 18px;
                    cursor: pointer;
                    display: none;
                }

                .fpl-floating-ad:hover .floating-close {
                    display: block;
                }

                .floating-content {
                    display: flex;
                    align-items: center;
                    gap: 12px;
                    color: white;
                }

                .floating-icon {
                    font-size: 2rem;
                }

                .floating-text {
                    display: flex;
                    flex-direction: column;
                }

                .floating-text strong {
                    font-size: 0.9rem;
                }

                .floating-text span {
                    font-size: 0.75rem;
                    opacity: 0.9;
                }

                /* Big Banner Popup */
                .fpl-big-banner-popup {
                    position: fixed;
                    top: 0;
                    left: 0;
                    width: 100%;
                    height: 100%;
                    z-index: 9999;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    animation: fadeIn 0.3s ease;
                }

                @keyframes fadeIn {
                    from { opacity: 0; }
                    to { opacity: 1; }
                }

                .fpl-big-banner-popup.closing {
                    animation: fadeOut 0.3s ease;
                }

                @keyframes fadeOut {
                    from { opacity: 1; }
                    to { opacity: 0; }
                }

                .popup-overlay {
                    position: absolute;
                    top: 0;
                    left: 0;
                    width: 100%;
                    height: 100%;
                    background: rgba(0, 0, 0, 0.7);
                    backdrop-filter: blur(5px);
                }

                .popup-content {
                    position: relative;
                    max-width: 600px;
                    width: 90%;
                    max-height: 90vh;
                    overflow-y: auto;
                    border-radius: 20px;
                    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
                    animation: slideUp 0.4s ease;
                }

                @keyframes slideUp {
                    from { transform: translateY(50px); opacity: 0; }
                    to { transform: translateY(0); opacity: 1; }
                }

                .popup-close {
                    position: absolute;
                    top: 15px;
                    right: 15px;
                    background: rgba(255, 255, 255, 0.2);
                    border: none;
                    color: white;
                    font-size: 30px;
                    width: 40px;
                    height: 40px;
                    border-radius: 50%;
                    cursor: pointer;
                    transition: all 0.3s ease;
                    z-index: 10;
