     9→async function injectHTML(filePath,elem) {
    10→    try {
    11→        const response = await fetch(filePath);
    12→        if (!response.ok) {
    13→            return;
    14→        }
    15→        const text = await response.text();
    16→        elem.innerHTML = text;
    17→        // reinject all <script> tags
    18→        // for each <script> tag on injected html
    19→        elem.querySelectorAll("script").forEach(script => {
    20→            // create a new empty <script> tag
    21→            const newScript = document.createElement("script");
    22→            // copy attributes of existing script tag 
    23→            // to a new one
    24→            Array.from(script.attributes).forEach(attr =>
    25→                newScript.setAttribute(attr.name, attr.value)
    26→            );
    27→            // inject a content of existing script tag 
    28→            // to a new one
    29→            newScript.appendChild(
    30→                document.createTextNode(script.innerHTML)
    31→            )
    32→            // replace existing script tag to a new one
    33→            script.parentNode.replaceChild(newScript, script);
    34→        })
    35→    } catch (err) {
    36→        console.error(err.message);
    37→    }
    38→}
