input.new_string:
/* FPL Tools Banner Styles (formerly Newsletter) */
.newsletter-banner {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    padding: 20px 0;
    position: relative;
    box-shadow: 0 4px 20px rgba(0,0,0,0.15);
    border-bottom: 3px solid rgba(255,255,255,0.2);
}

/* FPL Banner Features */
.fpl-banner-features {
    display: flex;
    gap: 12px;
    margin: 15px 0;
    flex-wrap: wrap;
}

.feature-badge {
    background: rgba(255,255,255,0.2);
    padding: 6px 12px;
    border-radius: 20px;
    font-size: 0.9rem;
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255,255,255,0.3);
}

/* FPL Banner CTAs */
.fpl-banner-cta {
    display: flex;
    gap: 15px;
    margin-top: 15px;
}

.fpl-banner-btn-primary {
    background: linear-gradient(135deg, #fff, #f0f0f0);
    color: #764ba2;
    padding: 12px 25px;
    border-radius: 25px;
    text-decoration: none;
    font-weight: 700;
    display: inline-block;
    transition: all 0.3s ease;
    box-shadow: 0 3px 10px rgba(0,0,0,0.2);
}

.fpl-banner-btn-primary:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(0,0,0,0.3);
    background: white;
}

.fpl-banner-btn-secondary {
    background: transparent;
    color: white;
    padding: 12px 25px;
    border-radius: 25px;
    text-decoration: none;
    font-weight: 600;
    display: inline-block;
    border: 2px solid rgba(255,255,255,0.5);
    transition: all 0.3s ease;
}

.fpl-banner-btn-secondary:hover {
    background: rgba(255,255,255,0.1);
    border-color: white;
}

/* Timer */
.fpl-banner-timer {
    margin-top: 10px;
    font-size: 0.9rem;
    opacity: 0.95;
    font-weight: 600;
}

#countdown {
    font-family: monospace;
    font-size: 1rem;
    background: rgba(255,255,255,0.2);
    padding: 2px 8px;
    border-radius: 4px;
}

