                .ai-sidebar-header {
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                    padding: 16px 20px;
                    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
                    color: white;
                }

                .ai-header-title {
                    display: flex;
                    align-items: center;
                    gap: 10px;
                }

                .ai-header-title h3 {
                    margin: 0;
                    font-size: 16px;
                    font-weight: 600;
                }

                .ai-close-btn {
                    background: none;
                    border: none;
                    color: white;
                    font-size: 28px;
                    cursor: pointer;
                    padding: 0;
                    width: 30px;
                    height: 30px;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    border-radius: 50%;
                    transition: background 0.2s;
                }

                .ai-close-btn:hover {
                    background: rgba(255, 255, 255, 0.2);
                }

                .ai-status-bar {
                    display: flex;
                    align-items: center;
                    gap: 10px;
                    padding: 10px 20px;
                    background: #f8f9fa;
                    border-bottom: 1px solid #e9ecef;
                    font-size: 12px;
                }

                .ai-status-indicator {
                    width: 8px;
                    height: 8px;
                    background: #51cf66;
                    border-radius: 50%;
                    animation: pulse 2s infinite;
                }

                .ai-message-counter {
                    margin-left: auto;
                    background: white;
                    padding: 4px 8px;
                    border-radius: 12px;
                    font-weight: 500;
                }

                .ai-quick-actions {
                    display: grid;
                    grid-template-columns: repeat(2, 1fr);
                    gap: 8px;
                    padding: 12px 20px;
                    background: #f8f9fa;
                    border-bottom: 1px solid #e9ecef;
                }

                .ai-quick-btn {
                    padding: 8px 12px;
                    background: white;
                    border: 1px solid #dee2e6;
                    border-radius: 8px;
                    font-size: 12px;
                    font-weight: 500;
                    color: #495057;
                    cursor: pointer;
                    transition: all 0.2s;
                }

                .ai-quick-btn:hover {
                    background: #667eea;
                    color: white;
                    border-color: #667eea;
                    transform: translateY(-1px);
