                .ai-message.user .message-bubble {
                    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
                    color: white;
                    padding: 10px 14px;
                    border-radius: 16px 16px 4px 16px;
                    font-size: 14px;
                    line-height: 1.4;
                }

                .ai-message.assistant {
                    align-self: flex-start;
                }

                .ai-message.assistant .message-bubble {
                    background: #f1f3f5;
                    color: #212529;
                    padding: 10px 14px;
                    border-radius: 16px 16px 16px 4px;
                    font-size: 14px;
                    line-height: 1.5;
                }

                .ai-typing-indicator {
                    display: none;
                    padding: 10px 20px;
                    gap: 4px;
                    align-items: center;
                }

                .ai-typing-indicator.active {
                    display: flex;
                }

                .ai-typing-indicator span {
                    width: 8px;
                    height: 8px;
                    background: #adb5bd;
                    border-radius: 50%;
                    animation: typing 1.4s infinite;
                }

                .ai-typing-indicator span:nth-child(2) {
                    animation-delay: 0.2s;
                }

                .ai-typing-indicator span:nth-child(3) {
                    animation-delay: 0.4s;
                }

                @keyframes typing {
                    0%, 60%, 100% {
                        transform: translateY(0);
                    }
                    30% {
                        transform: translateY(-10px);
                    }
                }

                .ai-input-container {
                    display: flex;
                    gap: 10px;
                    padding: 12px 20px;
                    background: #f8f9fa;
                    border-top: 1px solid #e9ecef;
                }

                .ai-input {
                    flex: 1;
                    padding: 10px 14px;
                    border: 1px solid #dee2e6;
                    border-radius: 20px;
                    font-size: 14px;
                    resize: none;
                    outline: none;
                    font-family: inherit;
                    min-height: 40px;
                    max-height: 100px;
                }

                .ai-input:focus {
                    border-color: #667eea;
                }

                .ai-send-btn {
                    width: 40px;
                    height: 40px;
                    border-radius: 50%;
                    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
                    border: none;
                    color: white;
                    cursor: pointer;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    transition: all 0.2s;
                }

                .ai-send-btn:hover {
                    transform: scale(1.1);
                }

                .ai-send-btn:disabled {
                    opacity: 0.5;
                    cursor: not-allowed;
                }

                .ai-upgrade-prompt {
                    padding: 16px 20px;
                    background: linear-gradient(135deg, #ffeaa7 0%, #fdcb6e 100%);
                    text-align: center;
                }

                .ai-upgrade-prompt p {
                    margin: 0 0 10px 0;
                    font-size: 13px;
                    font-weight: 500;
                    color: #2d3436;
                }

                .ai-upgrade-btn {
                    padding: 8px 20px;
                    background: #2d3436;
                    color: white;
                    border: none;
                    border-radius: 20px;
                    font-size: 13px;
                    font-weight: 600;
                    cursor: pointer;
                    transition: transform 0.2s;
                }

                .ai-upgrade-btn:hover {
                    transform: scale(1.05);
                }

                /* Mobile responsiveness */
                @media (max-width: 480px) {
                    .ai-sidebar {
                        right: 10px;
                        bottom: 10px;
                    }

                    .ai-sidebar-panel {
                        width: calc(100vw - 20px);
                        height: calc(100vh - 100px);
