   728‚Üí        handleQuickAction(action) {
   729‚Üí            const questions = {
   730‚Üí                captain: "Who should I captain this gameweek?",
   731‚Üí                transfers: "What transfers should I make this week?",
   732‚Üí                value: "Which players are the best value picks?",
   733‚Üí                fixtures: "Who has the best fixtures coming up?"
   734‚Üí            };
   735‚Üí
   736‚Üí            const question = questions[action];
   737‚Üí            if (question) {
   738‚Üí                this.input.value = question;
   739‚Üí                this.sendMessage();
   740‚Üí            }
   741‚Üí        }
   742‚Üí
   743‚Üí        sendMessage() {
   744‚Üí            const message = this.input.value.trim();
   745‚Üí            if (!message) return;
   746‚Üí
   747‚Üí            // Check message limit for free users
   748‚Üí            if (this.userType === 'free' && this.messageCount >= CONFIG.FREE_MESSAGE_LIMIT) {
   749‚Üí                this.showUpgradePrompt();
   750‚Üí                return;
   751‚Üí            }
   752‚Üí
   753‚Üí            // Add user message
   754‚Üí            this.addMessage(message, 'user');
   755‚Üí            this.input.value = '';
   756‚Üí            this.input.style.height = 'auto';
   757‚Üí
   758‚Üí            // Show typing indicator
   759‚Üí            this.showTypingIndicator();
   760‚Üí
   761‚Üí            // Simulate AI response
   762‚Üí            setTimeout(() => {
   763‚Üí                const response = this.generateResponse(message);
   764‚Üí                this.hideTypingIndicator();
   765‚Üí                this.addMessage(response, 'assistant');
   766‚Üí                
   767‚Üí                // Increment message count
   768‚Üí                this.messageCount++;
   769‚Üí                this.updateMessageCounter();
   770‚Üí                this.saveMessageHistory();
   771‚Üí            }, 1000 + Math.random() * 1500);
   772‚Üí        }
   773‚Üí
   774‚Üí        generateResponse(message) {
   775‚Üí            const lowerMessage = message.toLowerCase();
   776‚Üí            
   777‚Üí            // Determine response category
   778‚Üí            if (lowerMessage.includes('captain') || lowerMessage.includes('captaincy')) {
   779‚Üí                return this.getRandomResponse('captain');
   780‚Üí            } else if (lowerMessage.includes('transfer') || lowerMessage.includes('sell') || lowerMessage.includes('buy')) {
   781‚Üí                return this.getRandomResponse('transfers');
   782‚Üí            } else if (lowerMessage.includes('value') || lowerMessage.includes('budget') || lowerMessage.includes('cheap')) {
   783‚Üí                return this.getRandomResponse('value');
   784‚Üí            } else if (lowerMessage.includes('fixture') || lowerMessage.includes('schedule')) {
   785‚Üí                return this.getRandomResponse('fixtures');
   786‚Üí            } else if (lowerMessage.includes('premium') || lowerMessage.includes('expensive')) {
   787‚Üí                return this.getRandomResponse('premium');
   788‚Üí            } else if (lowerMessage.includes('differential') || lowerMessage.includes('unique')) {
   789‚Üí                return this.getRandomResponse('differential');
   790‚Üí            } else if (lowerMessage.includes('wildcard')) {
   791‚Üí                return this.getRandomResponse('wildcard');
   792‚Üí            } else if (lowerMessage.includes('chip') || lowerMessage.includes('bench boost') || lowerMessage.includes('triple captain')) {
   793‚Üí                return this.getRandomResponse('chips');
   794‚Üí            } else {
   795‚Üí                return this.getRandomResponse('general');
   796‚Üí            }
   797‚Üí        }
   798‚Üí
   799‚Üí        getRandomResponse(category) {
   800‚Üí            const responses = AI_RESPONSES[category] || AI_RESPONSES.general;
   801‚Üí            return responses[Math.floor(Math.random() * responses.length)];
   802‚Üí        }
