   804‚Üí        addMessage(text, sender) {
   805‚Üí            const messageDiv = document.createElement('div');
   806‚Üí            messageDiv.className = `ai-message ${sender}`;
   807‚Üí            
   808‚Üí            const bubble = document.createElement('div');
   809‚Üí            bubble.className = 'message-bubble';
   810‚Üí            bubble.textContent = text;
   811‚Üí            
   812‚Üí            messageDiv.appendChild(bubble);
   813‚Üí            this.messagesContainer.appendChild(messageDiv);
   814‚Üí            
   815‚Üí            // Scroll to bottom
   816‚Üí            this.messagesContainer.scrollTop = this.messagesContainer.scrollHeight;
   817‚Üí            
   818‚Üí            // Save to history
   819‚Üí            this.messages.push({ text, sender, timestamp: Date.now() });
   820‚Üí        }
