   832‚Üí        hideTypingIndicator() {
   833‚Üí            this.typingIndicator.classList.remove('active');
   834‚Üí        }
   835‚Üí
   836‚Üí        showUpgradePrompt() {
   837‚Üí            const upgradePrompt = this.sidebar.querySelector('.ai-upgrade-prompt');
   838‚Üí            if (upgradePrompt) {
   839‚Üí                upgradePrompt.style.display = 'block';
   840‚Üí            }
   841‚Üí            
   842‚Üí            this.input.disabled = true;
   843‚Üí            this.input.placeholder = 'Upgrade to Premium for unlimited messages';
   844‚Üí            
   845‚Üí            const sendBtn = this.sidebar.querySelector('#ai-send-btn');
   846‚Üí            if (sendBtn) sendBtn.disabled = true;
   847‚Üí        }
   848‚Üí
   849‚Üí        handleUpgrade() {
   850‚Üí            // Check if membership popup function exists
   851‚Üí            if (typeof window.showMembershipPopup === 'function') {
   852‚Üí                window.showMembershipPopup('AI Assistant');
   853‚Üí            } else {
   854‚Üí                // Fallback to direct navigation
   855‚Üí                window.location.href = '/fpl-premium-hub.html';
   856‚Üí            }
   857‚Üí        }
   858‚Üí
   859‚Üí        updateMessageCounter() {
   860‚Üí            const counter = this.sidebar.querySelector('.ai-message-counter');
   861‚Üí            if (counter) {
   862‚Üí                counter.textContent = this.getMessageCounterText();
   863‚Üí            }
   864‚Üí        }
   865‚Üí
   866‚Üí        getMessageCounterText() {
   867‚Üí            if (this.userType === 'premium') {
   868‚Üí                return '‚ú® Premium Member';
   869‚Üí            }
   870‚Üí            const remaining = CONFIG.FREE_MESSAGE_LIMIT - this.messageCount;
   871‚Üí            return `Free: ${remaining}/${CONFIG.FREE_MESSAGE_LIMIT} messages`;
   872‚Üí        }
   873‚Üí
   874‚Üí        checkUserStatus() {
   875‚Üí            // Check localStorage for user type
   876‚Üí            const userData = localStorage.getItem('fpl_user_data');
   877‚Üí            if (userData) {
   878‚Üí                try {
   879‚Üí                    const user = JSON.parse(userData);
   880‚Üí                    this.userType = user.subscription || 'free';
   881‚Üí                } catch (e) {
   882‚Üí                    this.userType = 'free';
   883‚Üí                }
   884‚Üí            }
   885‚Üí            
   886‚Üí            // Check for auth state if Firebase is available
   887‚Üí            if (typeof window.checkAuthState === 'function') {
   888‚Üí                window.checkAuthState((user) => {
   889‚Üí                    if (user && user.subscription) {
   890‚Üí                        this.userType = user.subscription;
   891‚Üí                        this.updateMessageCounter();
   892‚Üí                    }
   893‚Üí                });
   894‚Üí            }
   895‚Üí        }
   896‚Üí
   897‚Üí        loadMessageHistory() {
   898‚Üí            const stored = localStorage.getItem(CONFIG.STORAGE_KEY);
   899‚Üí            if (stored) {
   900‚Üí                try {
   901‚Üí                    const data = JSON.parse(stored);
   902‚Üí                    const today = new Date().toDateString();
   903‚Üí                    
   904‚Üí                    // Reset count if it's a new day
   905‚Üí                    if (data.date !== today) {
   906‚Üí                        this.messageCount = 0;
   907‚Üí                        localStorage.setItem(CONFIG.STORAGE_KEY, JSON.stringify({
   908‚Üí                            date: today,
   909‚Üí                            count: 0,
   910‚Üí                            messages: []
   911‚Üí                        }));
   912‚Üí                    } else {
   913‚Üí                        this.messageCount = data.count || 0;
   914‚Üí                        this.messages = data.messages || [];
   915‚Üí                        
   916‚Üí                        // Restore previous messages
   917‚Üí                        if (this.messages.length > 0) {
   918‚Üí                            this.messages.slice(-10).forEach(msg => {
   919‚Üí                                this.addMessage(msg.text, msg.sender);
   920‚Üí                            });
   921‚Üí                        }
   922‚Üí                    }
   923‚Üí                } catch (e) {
   924‚Üí                    this.messageCount = 0;
   925‚Üí                    this.messages = [];
   926‚Üí                }
   927‚Üí            }
   928‚Üí        }
   929‚Üí
   930‚Üí        saveMessageHistory() {
