   932‚Üí                date: new Date().toDateString(),
   933‚Üí                count: this.messageCount,
   934‚Üí                messages: this.messages.slice(-20) // Keep last 20 messages
   935‚Üí            };
   936‚Üí            localStorage.setItem(CONFIG.STORAGE_KEY, JSON.stringify(data));
   937‚Üí        }
