        init() {
            this.loadMessageHistory();
            this.checkUserStatus();
            this.createSidebarHTML();
            this.attachEventListeners();
            this.addWelcomeMessage();

            // Auto-open on desktop if previously open
            if (window.innerWidth > 768 && this.isOpen) {
                this.openSidebar();
