                        </div>
                        <button class="ai-minimize-btn" title="Minimize">
                            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                                <path d="M15 18l-6-6 6-6" stroke-width="2"/>
                            </svg>
                        </button>
                    </div>

                    <!-- Message Counter -->
