                .banner-cta-secondary:hover {
                    background: rgba(255, 255, 255, 0.1);
                    border-color: rgba(255, 255, 255, 0.5);
                }

                .banner-special-badge {
                    position: absolute;
                    top: 15px;
                    left: 15px;
                    background: linear-gradient(135deg, #ff3366, #ff6633);
                    color: white;
                    padding: 6px 15px;
                    border-radius: 15px;
                    font-size: 0.75rem;
                    font-weight: 700;
                    animation: pulse 2s infinite;
                }

                /* Countdown Timer Styles */
                .countdown-timer {
                    margin: 25px 0;
                    text-align: center;
                }

                .countdown-label {
                    color: white;
                    font-size: 1rem;
                    font-weight: 600;
                    margin-bottom: 15px;
                    opacity: 0.95;
                }

                .countdown-display {
                    display: flex;
                    justify-content: center;
                    gap: 20px;
                    flex-wrap: wrap;
                }

                .time-unit {
                    background: rgba(255, 255, 255, 0.15);
                    padding: 12px 16px;
                    border-radius: 12px;
                    backdrop-filter: blur(10px);
                    border: 1px solid rgba(255, 255, 255, 0.2);
                    min-width: 60px;
                }

                .time-value {
                    display: block;
                    font-size: 1.8rem;
                    font-weight: 800;
                    color: white;
                    line-height: 1;
                    margin-bottom: 4px;
                }

                .time-label {
                    display: block;
                    font-size: 0.7rem;
                    color: rgba(255, 255, 255, 0.8);
                    font-weight: 600;
                    text-transform: uppercase;
                    letter-spacing: 0.5px;
                }

                /* Banner Ads */
                .fpl-banner-ad {
                    border-radius: 12px;
                    padding: 25px;
                    margin: 30px 0;
                    color: white;
                    box-shadow: 0 5px 20px rgba(0,0,0,0.2);
                }

                .banner-content {
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                    flex-wrap: wrap;
                    gap: 20px;
                }

                .banner-left h3 {
                    font-size: 1.5rem;
                    margin-bottom: 5px;
                }

                .banner-left p {
                    opacity: 0.95;
                }

                .banner-cta {
                    background: rgba(255,255,255,0.2);
                    color: white;
                    padding: 12px 25px;
                    border-radius: 8px;
                    text-decoration: none;
                    font-weight: 700;
                    backdrop-filter: blur(10px);
                    transition: all 0.3s ease;
                    display: inline-block;
                }

                .banner-cta:hover {
                    background: rgba(255,255,255,0.3);
                    transform: scale(1.05);
                }

                /* Responsive */
                @media (max-width: 768px) {
                    .fpl-floating-ad {
                        bottom: 10px;
                        right: 10px;
                        padding: 10px 15px;
                    }

                    .floating-text {
                        display: none;
                    }

                    .banner-content {
                        flex-direction: column;
                        text-align: center;
                    }

                    .fpl-sidebar-ad {
                        margin: 10px;
                    }

                    /* Big Banner Popup Mobile */
                    .popup-content {
                        width: 95%;
                        max-height: 95vh;
                    }

                    .popup-inner {
                        padding: 25px;
                    }

                    .popup-icon {
                        font-size: 3rem;
                    }

                    .popup-title {
                        font-size: 1.8rem;
                    }

                    .popup-subtitle {
                        font-size: 1rem;
                    }

                    .popup-description {
                        font-size: 0.95rem;
                    }

                    .popup-cta-group {
                        flex-direction: column;
                        width: 100%;
                    }

                    .popup-cta-primary,
                    .popup-cta-secondary {
                        width: 100%;
                    }

                    /* Big Banner Mobile */
                    .banner-container {
                        padding: 20px 15px;
                    }

                    .banner-content {
                        grid-template-columns: 1fr;
                        gap: 20px;
                        text-align: center;
                    }

                    .banner-left {
                        flex-direction: column;
                        gap: 10px;
                    }

                    .banner-icon {
                        font-size: 2.5rem;
                    }

                    .banner-title {
                        font-size: 1.4rem;
                    }

                    .banner-subtitle {
                        font-size: 0.9rem;
                    }

                    .banner-description {
                        font-size: 0.8rem;
                    }

                    .banner-features {
                        gap: 8px;
                    }

                    .banner-features .feature-item {
                        padding: 6px 10px;
                        font-size: 0.75rem;
                        gap: 3px;
                    }

                    .feature-icon {
                        font-size: 0.9rem;
                    }

                    .banner-special-badge {
                        font-size: 0.65rem;
                        padding: 4px 10px;
                        top: 10px;
                        left: 10px;
                    }

                    /* Countdown Timer Mobile */
                    .countdown-display {
                        gap: 8px;
                    }

                    .time-unit {
                        padding: 6px 10px;
                        min-width: 45px;
                    }

