            left: -44px;
        }

        .toggle-label {
            font-size: 11px;
        }

        .ai-quick-actions {
            grid-template-columns: repeat(2, 1fr);
        }

        .ai-message {
            max-width: 90%;
        }
    }

    /* Adjust main content when sidebar is open */
    body.ai-sidebar-open {
        margin-right: 400px;
        transition: margin-right 0.3s ease;
    }

    @media (max-width: 768px) {
        body.ai-sidebar-open {
            margin-right: 0;
            overflow: hidden;
        }
    }
