        getFixtureAnalysis() {
            let response = "üìÖ **Fixture Difficulty Analysis:**\n\n";

            const sortedTeams = Object.entries(this.fixtureData)
                .sort((a, b) => a[1].avgDifficulty - b[1].avgDifficulty)
