  1103→        // Enhanced Player Data Pro functionality
  1104→        class PlayerDataPro {
  1105→            constructor() {
  1106→                this.players = [];
  1107→                this.filteredPlayers = [];
  1108→                this.selectedPlayers = [];
  1109→                this.charts = {};
  1110→                this.init();
  1111→            }
  1112→            
  1113→            async init() {
  1114→                this.showLoading();
  1115→                await this.loadData();
  1116→                this.setupEventListeners();
  1117→                this.initCharts();
  1118→                this.hideLoading();
  1119→            }
  1120→            
  1121→            async loadData() {
  1122→                try {
  1123→                    const fplService = new FPLDataService();
  1124→                    
  1125→                    // Try to load real data first
  1126→                    console.log('Attempting to load live FPL data...');
  1127→                    const data = await fplService.getBootstrapData();
  1128→                    
  1129→                    if (data && data.elements && data.elements.length > 0) {
  1130→                        console.log('Successfully loaded live FPL data');
  1131→                        this.processPlayerData(data);
  1132→                    } else {
  1133→                        throw new Error('Invalid data structure received');
  1134→                    }
  1135→                    
  1136→                } catch (error) {
  1137→                    console.error('Failed to load live data, using mock data:', error);
  1138→                    
  1139→                    // Use mock data as fallback
  1140→                    const fplService = new FPLDataService();
  1141→                    const mockData = fplService.getMockBootstrapData();
  1142→                    this.processPlayerData(mockData);
  1143→                    
  1144→                    // Show notification that we're using mock data
  1145→                    this.showMockDataNotification();
  1146→                }
  1147→                
  1148→                this.updateDashboard();
  1149→                this.renderTable();
  1150→            }
  1151→            
  1152→            showMockDataNotification() {
  1153→                const notification = document.createElement('div');
  1154→                notification.style.cssText = `
  1155→                    position: fixed;
  1156→                    top: 80px;
  1157→                    right: 20px;
  1158→                    background: linear-gradient(135deg, #fbbf24, #f59e0b);
  1159→                    color: white;
  1160→                    padding: 15px 20px;
  1161→                    border-radius: 10px;
  1162→                    box-shadow: 0 10px 30px rgba(0,0,0,0.2);
  1163→                    z-index: 10000;
  1164→                    font-weight: 600;
  1165→                    max-width: 300px;
  1166→                `;
  1167→                notification.innerHTML = `
  1168→                    <div style="display: flex; align-items: center; gap: 10px;">
  1169→                        <span>⚠️</span>
  1170→                        <div>
  1171→                            <div>Demo Mode Active</div>
  1172→                            <div style="font-size: 0.8rem; opacity: 0.9; margin-top: 2px;">
  1173→                                Using sample data for demonstration
  1174→                            </div>
  1175→                        </div>
  1176→                    </div>
  1177→                `;
  1178→                
  1179→                document.body.appendChild(notification);
  1180→                
  1181→                // Auto-remove after 5 seconds
  1182→                setTimeout(() => {
  1183→                    if (notification.parentElement) {
  1184→                        notification.style.animation = 'fadeOut 0.3s ease';
  1185→                        setTimeout(() => notification.remove(), 300);
  1186→                    }
  1187→                }, 5000);
  1188→            }
  1189→            
  1190→            processPlayerData(data) {
  1191→                // Store teams data for mapping
  1192→                this.teams = {};
  1193→                if (data.teams) {
  1194→                    data.teams.forEach(team => {
  1195→                        this.teams[team.id] = team;
  1196→                    });
  1197→                }
  1198→                
  1199→                // Store element types for mapping
  1200→                this.elementTypes = {};
  1201→                if (data.element_types) {
  1202→                    data.element_types.forEach(type => {
  1203→                        this.elementTypes[type.id] = type;
  1204→                    });
  1205→                }
  1206→                
  1207→                this.players = data.elements.map(player => {
  1208→                    const team = this.teams[player.team] || { short_name: `T${player.team}` };
  1209→                    const price = player.now_cost / 10;
  1210→                    const totalPoints = player.total_points || 0;
  1211→                    
  1212→                    return {
  1213→                        id: player.id,
  1214→                        name: `${player.first_name} ${player.second_name}`.trim(),
  1215→                        team: player.team,
  1216→                        teamName: team.short_name || team.name || `Team ${player.team}`,
  1217→                        position: this.getPositionName(player.element_type),
  1218→                        price: price,
  1219→                        points: totalPoints,
  1220→                        form: parseFloat(player.form || 0),
  1221→                        ownership: parseFloat(player.selected_by_percent || 0),
  1222→                        value: totalPoints > 0 ? (totalPoints / price).toFixed(1) : '0.0',
  1223→                        ict: parseFloat(player.ict_index || 0),
  1224→                        goals: player.goals_scored || 0,
  1225→                        assists: player.assists || 0,
  1226→                        cleanSheets: player.clean_sheets || 0,
  1227→                        saves: player.saves || 0,
  1228→                        bonus: player.bonus || 0,
  1229→                        minutes: player.minutes || 0,
  1230→                        transfersIn: player.transfers_in_event || 0,
  1231→                        transfersOut: player.transfers_out_event || 0,
  1232→                        chanceOfPlaying: player.chance_of_playing_next_round || 100
  1233→                    };
  1234→                });
  1235→                
  1236→                // Sort players by total points descending
  1237→                this.players.sort((a, b) => b.points - a.points);
  1238→                this.filteredPlayers = [...this.players];
  1239→                
  1240→                console.log(`Processed ${this.players.length} players`);
  1241→            }
  1242→            
  1243→            getPositionName(type) {
  1244→                const positions = ['', 'GKP', 'DEF', 'MID', 'FWD'];
  1245→                return positions[type] || 'Unknown';
  1246→            }
  1247→            
  1248→            updateDashboard() {
  1249→                // Update metrics
  1250→                document.getElementById('totalPlayers').textContent = this.filteredPlayers.length;
  1251→                
  1252→                const avgPoints = (this.filteredPlayers.reduce((sum, p) => sum + p.points, 0) / this.filteredPlayers.length).toFixed(1);
  1253→                document.getElementById('avgPoints').textContent = avgPoints;
  1254→                
  1255→                const topPerformer = this.filteredPlayers.reduce((max, p) => p.points > max.points ? p : max);
