  1266→            updateTrendingPlayers() {
  1267→                const trending = [...this.players]
  1268→                    .sort((a, b) => b.transfersIn - a.transfersIn)
  1269→                    .slice(0, 3);
  1270→                
  1271→                const list = document.getElementById('trendingList');
  1272→                list.innerHTML = trending.map((player, index) => `
  1273→                    <li class="trending-item">
