  1444→                    if (player.price < minPrice || player.price > maxPrice) return false;
  1445→                    
  1446→                    if (form !== 'all') {
  1447→                        if (form === 'hot' && player.form <= 7) return false;
  1448→                        if (form === 'good' && (player.form < 5 || player.form > 7)) return false;
  1449→                        if (form === 'average' && (player.form < 3 || player.form > 5)) return false;
  1450→                        if (form === 'poor' && player.form >= 3) return false;
  1451→                    }
  1452→                    
  1453→                    if (ownership !== 'all') {
