  1456→                        if (ownership === 'medium' && (player.ownership < 20 || player.ownership > 40)) return false;
  1457→                        if (ownership === 'high' && player.ownership <= 40) return false;
  1458→                    }
  1459→                    
  1460→                    if (availability !== 'all') {
