  1462→                        if (availability === 'doubtful' && player.chanceOfPlaying !== 75) return false;
  1463→                        if (availability === 'injured' && player.chanceOfPlaying >= 75) return false;
  1464→                    }
  1465→                    
  1466→                    return true;
  1467→                });
