   250â†’    function headerSignOut() {
   251â†’        // Clear user data
   252â†’        localStorage.removeItem('user');
   253â†’        localStorage.removeItem('membership');
   254â†’        localStorage.removeItem('eplMembership');
   255â†’        localStorage.removeItem('authToken');
   256â†’        
   257â†’        // Hide account widget
   258â†’        hideHeaderAccountWidget();
   259â†’        
   260â†’        // Update header state
   261â†’        showSignedOutState();
   262â†’        
   263â†’        // Show notification
   264â†’        showHeaderNotification('Signed out successfully!', 'success');
   265â†’        
   266â†’        // Reload page to reset state
   267â†’        setTimeout(() => {
   268â†’            window.location.reload();
   269â†’        }, 1000);
   270â†’    }
   271â†’
   272â†’    function showHeaderNotification(message, type = 'info') {
   273â†’        const notification = document.createElement('div');
   274â†’        notification.className = `header-notification ${type}`;
   275â†’        notification.innerHTML = `
   276â†’            <span>${message}</span>
   277â†’            <button onclick="this.parentElement.remove()">Ã—</button>
   278â†’        `;
   279â†’        notification.style.cssText = `
   280â†’            position: fixed;
   281â†’            top: 20px;
   282â†’            right: 20px;
   283â†’            background: ${type === 'success' ? '#28a745' : type === 'error' ? '#dc3545' : '#007bff'};
   284â†’            color: white;
   285â†’            padding: 15px 20px;
   286â†’            border-radius: 8px;
   287â†’            box-shadow: 0 4px 15px rgba(0,0,0,0.2);
   288â†’            z-index: 10000;
   289â†’            display: flex;
   290â†’            align-items: center;
   291â†’            gap: 10px;
   292â†’            font-weight: 600;
   293â†’            animation: slideInRight 0.3s ease;
   294â†’        `;
   295â†’        
   296â†’        document.body.appendChild(notification);
   297â†’        
   298â†’        // Auto-remove after 3 seconds
   299â†’        setTimeout(() => {
   300â†’            if (notification.parentElement) {
   301â†’                notification.style.animation = 'slideOutRight 0.3s ease';
   302â†’                setTimeout(() => notification.remove(), 300);
   303â†’            }
   304â†’        }, 3000);
   305â†’    }
   306â†’
   307â†’    // Add CSS for hidden class if not already defined
   308â†’    if (!document.querySelector('style[data-header-auth]')) {
   309â†’        const style = document.createElement('style');
   310â†’        style.setAttribute('data-header-auth', 'true');
   311â†’        style.textContent = `
