  2001â†’    function formatDate(dateStr) {
  2002â†’        const date = new Date(dateStr);
  2003â†’        const now = new Date();
  2004â†’        const diff = now - date;
  2005â†’        const days = Math.floor(diff / (1000 * 60 * 60 * 24));
  2006â†’        
  2007â†’        if (days === 0) return 'Today';
  2008â†’        if (days === 1) return 'Yesterday';
  2009â†’        if (days < 7) return `${days} days ago`;
  2010â†’        return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
  2011â†’    }
