        const parentRect = dropdownParent.getBoundingClientRect();
        const viewportWidth = window.innerWidth;
        const dropdownWidth = 260; // Average dropdown width

        // Calculate optimal position
        const parentCenter = parentRect.left + (parentRect.width / 2);
        const leftEdge = parentCenter - (dropdownWidth / 2);
        const rightEdge = parentCenter + (dropdownWidth / 2);

        // Reset inline styles to let CSS handle it
