   124→<script>
   125→    // Check authentication state and update header
   126→    document.addEventListener('DOMContentLoaded', function() {
   127→        checkHeaderAuthState();
   128→        checkPremiumMembershipForAds();
   129→    });
   130→
   131→    function checkHeaderAuthState() {
   132→        // Check for user data in localStorage
   133→        const userData = localStorage.getItem('user');
   134→        
   135→        if (userData) {
   136→            try {
   137→                const user = JSON.parse(userData);
   138→                showSignedInState(user);
   139→                showHeaderAccountWidget(user);
   140→            } catch (error) {
   141→                console.error('Error parsing user data:', error);
   142→                showSignedOutState();
   143→                hideHeaderAccountWidget();
   144→            }
   145→        } else {
   146→            showSignedOutState();
   147→            hideHeaderAccountWidget();
   148→        }
   149→    }
   150→
   151→    function showSignedInState(user) {
   152→        // Hide sign in/create account links
   153→        const signinLink = document.getElementById('signin-link');
   154→        const createAccountLink = document.getElementById('create-account-link');
   155→        const mobileSigninLink = document.getElementById('mobile-signin-link');
   156→        const mobileCreateAccountLink = document.getElementById('mobile-create-account-link');
   157→        
   158→        if (signinLink) signinLink.classList.add('hidden');
   159→        if (createAccountLink) createAccountLink.classList.add('hidden');
   160→        if (mobileSigninLink) mobileSigninLink.classList.add('hidden');
   161→        if (mobileCreateAccountLink) mobileCreateAccountLink.classList.add('hidden');
   162→        
   163→        // Show account and premium links
   164→        const accountLink = document.getElementById('account-link');
   165→        const premiumLink = document.getElementById('premium-link');
   166→        const mobileAccountLink = document.getElementById('mobile-account-link');
   167→        const mobilePremiumLink = document.getElementById('mobile-premium-link');
   168→        
   169→        if (accountLink) accountLink.classList.remove('hidden');
   170→        if (premiumLink) premiumLink.classList.remove('hidden');
   171→        if (mobileAccountLink) mobileAccountLink.classList.remove('hidden');
   172→        if (mobilePremiumLink) mobilePremiumLink.classList.remove('hidden');
   173→    }
   174→
   175→    function showSignedOutState() {
   176→        // Show sign in/create account links
   177→        const signinLink = document.getElementById('signin-link');
   178→        const createAccountLink = document.getElementById('create-account-link');
   179→        const mobileSigninLink = document.getElementById('mobile-signin-link');
   180→        const mobileCreateAccountLink = document.getElementById('mobile-create-account-link');
   181→        
   182→        if (signinLink) signinLink.classList.remove('hidden');
   183→        if (createAccountLink) createAccountLink.classList.remove('hidden');
   184→        if (mobileSigninLink) mobileSigninLink.classList.remove('hidden');
   185→        if (mobileCreateAccountLink) mobileCreateAccountLink.classList.remove('hidden');
   186→        
   187→        // Hide account and premium links
   188→        const accountLink = document.getElementById('account-link');
   189→        const premiumLink = document.getElementById('premium-link');
   190→        const mobileAccountLink = document.getElementById('mobile-account-link');
   191→        const mobilePremiumLink = document.getElementById('mobile-premium-link');
   192→        
   193→        if (accountLink) accountLink.classList.add('hidden');
   194→        if (premiumLink) premiumLink.classList.add('hidden');
   195→        if (mobileAccountLink) mobileAccountLink.classList.add('hidden');
   196→        if (mobilePremiumLink) mobilePremiumLink.classList.add('hidden');
   197→    }
   198→
   199→    function checkPremiumMembershipForAds() {
   200→        try {
   201→            // Check for membership data
   202→            const membershipData = localStorage.getItem('membership') || localStorage.getItem('eplMembership');
   203→            
   204→            if (membershipData) {
   205→                const membership = JSON.parse(membershipData);
   206→                
   207→                // If user has premium membership, hide ads
   208→                if (membership.plan && membership.plan !== 'free') {
   209→                    hidePremiumAds();
   210→                    showAdFreeIndicator();
   211→                }
   212→            }
   213→        } catch (error) {
   214→            console.error('Error checking membership for ads:', error);
   215→        }
   216→    }
   217→
   218→    function hidePremiumAds() {
   219→        // Hide Google AdSense ads
   220→        const adElements = document.querySelectorAll('.adsbygoogle, [class*="ad-"], [id*="ad-"], [class*="advertisement"], [class*="google-ads"]');
   221→        adElements.forEach(ad => {
   222→            ad.style.display = 'none';
   223→            ad.style.visibility = 'hidden';
   224→        });
   225→
   226→        // Also hide any ad containers or wrappers
   227→        const adContainers = document.querySelectorAll('[class*="ad-container"], [class*="ad-wrapper"], [id*="ad-container"], [id*="ad-wrapper"]');
   228→        adContainers.forEach(container => {
   229→            container.style.display = 'none';
   230→        });
   231→
   232→        // Add a class to body to indicate ad-free mode
   233→        document.body.classList.add('ad-free-premium');
   234→        
   235→        console.log('Premium ad-free experience activated');
   236→    }
   237→
   238→    function showAdFreeIndicator() {
   239→        // Ad-free indicator popup removed
   240→    }
   241→
   242→    function showHeaderAccountWidget(user) {
   243→        // Account widget popup removed
   244→    }
   245→
   246→    function hideHeaderAccountWidget() {
   247→        // Account widget popup removed
