   250→    function headerSignOut() {
   251→        // Clear user data
   252→        localStorage.removeItem('user');
   253→        localStorage.removeItem('membership');
   254→        localStorage.removeItem('eplMembership');
   255→        localStorage.removeItem('authToken');
   256→        
   257→        // Hide account widget
   258→        hideHeaderAccountWidget();
   259→        
   260→        // Update header state
   261→        showSignedOutState();
   262→        
   263→        // Show notification
   264→        showHeaderNotification('Signed out successfully!', 'success');
   265→        
   266→        // Reload page to reset state
   267→        setTimeout(() => {
   268→            window.location.reload();
   269→        }, 1000);
   270→    }
