  1740→<script>
  1741→    // Enhanced Search Functionality
  1742→    let searchTimeout;
  1743→    let isSearching = false;
  1744→    let searchCache = new Map();
  1745→    
  1746→    // Initialize search functionality
  1747→    document.addEventListener('DOMContentLoaded', function() {
  1748→        initializeSearch();
  1749→    });
  1750→    
  1751→    function initializeSearch() {
  1752→        const searchInput = document.getElementById('search-input');
  1753→        const searchBtn = document.querySelector('.search-btn');
  1754→        const searchClear = document.querySelector('.search-clear');
  1755→        
  1756→        if (searchInput) {
  1757→            // Add event listeners
  1758→            searchInput.addEventListener('input', handleSearchInput);
  1759→            searchInput.addEventListener('keydown', handleSearchKeydown);
  1760→            searchInput.addEventListener('focus', handleSearchFocus);
  1761→            searchInput.addEventListener('blur', handleSearchBlur);
  1762→        }
  1763→        
  1764→        if (searchClear) {
  1765→            searchClear.addEventListener('click', clearSearch);
  1766→        }
  1767→        
  1768→        // Close dropdown when clicking outside
  1769→        document.addEventListener('click', function(event) {
  1770→            const searchContainer = document.querySelector('.search-container');
  1771→            if (searchContainer && !searchContainer.contains(event.target)) {
  1772→                hideSearchResults();
  1773→            }
  1774→        });
  1775→    }
  1776→    
  1777→    function handleSearchInput(event) {
  1778→        const query = event.target.value;
  1779→        const searchClear = document.querySelector('.search-clear');
  1780→        
  1781→        // Show/hide clear button
  1782→        if (searchClear) {
  1783→            searchClear.style.display = query.length > 0 ? 'flex' : 'none';
  1784→        }
  1785→        
  1786→        // Debounced search
  1787→        clearTimeout(searchTimeout);
  1788→        if (query.length >= 2) {
  1789→            searchTimeout = setTimeout(() => {
  1790→                performInstantSearch(query);
  1791→            }, 300);
  1792→        } else {
  1793→            hideSearchResults();
  1794→        }
  1795→    }
  1796→    
  1797→    function handleSearchKeydown(event) {
  1798→        if (event.key === 'Enter') {
  1799→            event.preventDefault();
  1800→            const query = event.target.value.trim();
  1801→            if (query) {
  1802→                performSearch(query);
  1803→            }
  1804→        } else if (event.key === 'Escape') {
  1805→            hideSearchResults();
  1806→            event.target.blur();
  1807→        }
  1808→    }
  1809→    
  1810→    function handleSearchFocus(event) {
  1811→        const query = event.target.value;
  1812→        if (query.length >= 2) {
  1813→            performInstantSearch(query);
  1814→        }
  1815→    }
  1816→    
  1817→    function handleSearchBlur(event) {
  1818→        // Delay hiding to allow click events on results
  1819→        setTimeout(() => {
  1820→            if (!document.querySelector('.search-results-dropdown:hover')) {
  1821→                hideSearchResults();
  1822→            }
  1823→        }, 200);
  1824→    }
  1825→    
  1826→    function clearSearch() {
  1827→        const searchInput = document.getElementById('search-input');
  1828→        const searchClear = document.querySelector('.search-clear');
  1829→        
  1830→        if (searchInput) {
  1831→            searchInput.value = '';
  1832→            searchInput.focus();
  1833→        }
  1834→        if (searchClear) {
  1835→            searchClear.style.display = 'none';
  1836→        }
  1837→        hideSearchResults();
