function adjustDropdownPosition() {
    const dropdown = document.querySelector('.dropdown-menu');
    const dropdownParent = document.querySelector('.dropdown');

    if (dropdown && dropdownParent) {
        const rect = dropdown.getBoundingClientRect();
        const viewportWidth = window.innerWidth;
        const dropdownWidth = dropdown.offsetWidth;

        // Reset positioning
        dropdown.style.left = '';
        dropdown.style.right = '';
        dropdown.style.transform = '';

        // Check if dropdown would overflow on the right
        if (rect.right > viewportWidth - 20) {
            dropdown.style.left = 'auto';
            dropdown.style.right = '0';
            dropdown.style.transform = 'translateY(-10px) scale(0.95)';
            dropdown.style.transformOrigin = 'top right';
        } 
        // Check if dropdown would overflow on the left  
        else if (rect.left < 20) {
            dropdown.style.left = '0';
            dropdown.style.right = 'auto';
            dropdown.style.transform = 'translateY(-10px) scale(0.95)';
            dropdown.style.transformOrigin = 'top left';
        }
        // Default centered positioning
        else {
            dropdown.style.left = '50%';
            dropdown.style.right = 'auto';
            dropdown.style.transform = 'translateX(-50%) translateY(-10px) scale(0.95)';
            dropdown.style.transformOrigin = 'top center';
        }
    }
}

