            dropdown.style.left = '0';
            dropdown.style.right = 'auto';
            dropdown.style.transform = 'translateY(-15px) scale(0.92)';
            dropdown.style.transformOrigin = 'top left';
        }
        // Otherwise, let CSS handle centered positioning
    }
}

