// Handle FPL dropdown on touch devices
document.addEventListener('DOMContentLoaded', function() {
    const fplDropdown = document.querySelector('.dropdown');
    const fplLink = document.querySelector('.fpl-premium-nav');

    // Add hover and resize listeners for smart positioning
    if (fplDropdown) {
        fplDropdown.addEventListener('mouseenter', adjustDropdownPosition);
        window.addEventListener('resize', adjustDropdownPosition);
    }

    if (fplDropdown && fplLink) {
        // For touch devices - toggle dropdown on click but allow navigation
        let clickCount = 0;
        fplLink.addEventListener('click', function(e) {
            if (window.innerWidth <= 1024) {
                const dropdownMenu = fplDropdown.querySelector('.dropdown-menu');
                if (dropdownMenu) {
                    const isVisible = dropdownMenu.style.opacity === '1';
                    if (!isVisible) {
                        // First click - show dropdown
                        e.preventDefault();
                        dropdownMenu.style.opacity = '1';
                        dropdownMenu.style.visibility = 'visible';
                        dropdownMenu.style.pointerEvents = 'auto';
                        clickCount = 1;
                    } else if (clickCount === 1) {
                        // Second click - navigate to page
                        clickCount = 0;
                        // Let the default navigation happen
                    } else {
                        // Hide dropdown
                        e.preventDefault();
                        dropdownMenu.style.opacity = '0';
                        dropdownMenu.style.visibility = 'hidden';
                        dropdownMenu.style.pointerEvents = 'none';
                        clickCount = 0;
                    }
                }
            }
            // On desktop, always allow navigation
        });

        // Close dropdown when clicking outside
        document.addEventListener('click', function(e) {
            if (!fplDropdown.contains(e.target)) {
                const dropdownMenu = fplDropdown.querySelector('.dropdown-menu');
                if (dropdownMenu) {
                    dropdownMenu.style.opacity = '';
                    dropdownMenu.style.visibility = '';
                    dropdownMenu.style.pointerEvents = '';
                }
            }
        });
    }
})

edits.new_string:
// Enhanced FPL dropdown handling
document.addEventListener('DOMContentLoaded', function() {
    const fplDropdown = document.querySelector('.dropdown');
    const fplLink = document.querySelector('.fpl-premium-nav');
    const dropdownMenu = fplDropdown ? fplDropdown.querySelector('.dropdown-menu') : null;

    if (!fplDropdown || !fplLink || !dropdownMenu) return;

    let isOpen = false;
    let touchStarted = false;
    let closeTimeout = null;

    // Smooth dropdown toggle function
    function toggleDropdown(show) {
        clearTimeout(closeTimeout);

        if (show) {
            fplDropdown.classList.add('active');
            adjustDropdownPosition();
            isOpen = true;
        } else {
            fplDropdown.classList.remove('active');
            isOpen = false;
        }
    }

    // Desktop hover handling
    if (window.innerWidth > 1024) {
        fplDropdown.addEventListener('mouseenter', function() {
            toggleDropdown(true);
        });

        fplDropdown.addEventListener('mouseleave', function() {
            closeTimeout = setTimeout(() => {
                toggleDropdown(false);
            }, 100);
        });
    }

    // Touch device handling
    fplLink.addEventListener('touchstart', function(e) {
        touchStarted = true;
    });

    fplLink.addEventListener('click', function(e) {
        if (window.innerWidth <= 1024 || touchStarted) {
            e.preventDefault();
            e.stopPropagation();
            toggleDropdown(!isOpen);
            touchStarted = false;
        }
    });

    // Dropdown item clicks should close dropdown
    dropdownMenu.querySelectorAll('.dropdown-item').forEach(item => {
        item.addEventListener('click', function() {
            toggleDropdown(false);
        });
    });

    // Close on outside click
    document.addEventListener('click', function(e) {
        if (!fplDropdown.contains(e.target)) {
            toggleDropdown(false);
        }
    });

    // Close on escape key
    document.addEventListener('keydown', function(e) {
        if (e.key === 'Escape' && isOpen) {
            toggleDropdown(false);
        }
    });

    // Adjust position on window resize
    let resizeTimeout;
    window.addEventListener('resize', function() {
        clearTimeout(resizeTimeout);
        resizeTimeout = setTimeout(() => {
            if (isOpen) {
                adjustDropdownPosition();
            }
        }, 150);
    });

    // Keyboard navigation support
    fplLink.addEventListener('keydown', function(e) {
        if (e.key === 'Enter' || e.key === ' ') {
            e.preventDefault();
            toggleDropdown(!isOpen);
        }
    });
})

