.dropdown:hover .dropdown-menu,
.dropdown:focus-within .dropdown-menu,
.dropdown.active .dropdown-menu {
    opacity: 1 !important;
    visibility: visible !important;
    pointer-events: auto !important;
    transform: translateX(-50%) translateY(0) scale(1) !important;
    animation: dropdownSlideIn 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94) forwards !important;
}

@keyframes dropdownSlideIn {
    0% {
        opacity: 0;
        transform: translateX(-50%) translateY(-15px) scale(0.92);
    }
    60% {
        opacity: 1;
        transform: translateX(-50%) translateY(2px) scale(1.02);
    }
    100% {
        opacity: 1;
        transform: translateX(-50%) translateY(0) scale(1);
    }
}

.dropdown-item {
    display: flex;
    align-items: center;
    padding: 14px 20px;
    text-decoration: none;
    color: #333;
    transition: all 0.35s cubic-bezier(0.25, 0.46, 0.45, 0.94);
    position: relative;
    border-bottom: 1px solid rgba(0,0,0,0.04);
    gap: 12px;
    white-space: nowrap;
    overflow: hidden;
    background: transparent;
    will-change: padding-left, background-color;
}

.dropdown-item::before {
    content: '';
    position: absolute;
    left: 0;
    top: 0;
    height: 100%;
    width: 0;
    background: linear-gradient(90deg, rgba(56, 0, 60, 0.1), rgba(0, 255, 135, 0.05));

