    /* Team Picker Styles */
    .team-picker-section {
        margin-top: 50px;
        padding: 30px;
        background: linear-gradient(135deg, #0f1419 0%, #1a1f2e 100%);
        border-radius: 20px;
        box-shadow: 0 10px 30px rgba(0,0,0,0.4);
    }

    .section-header {
        text-align: center;
        margin-bottom: 30px;
    }

    .section-header h2 {
        font-size: 2.5rem;
        margin-bottom: 10px;
        background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;
    }

    .section-header p {
        color: #b0b3b8;
        font-size: 1.1rem;
    }

    .team-builder-container {
        background: rgba(255,255,255,0.03);
        border-radius: 16px;
        padding: 25px;
    }

    .team-summary {
        display: flex;
        justify-content: space-around;
        margin-bottom: 25px;
        padding: 20px;
        background: rgba(103,126,234,0.1);
        border-radius: 12px;
        border: 1px solid rgba(103,126,234,0.3);
    }

    .summary-item {
        text-align: center;
    }

    .summary-label {
        display: block;
        color: #b0b3b8;
        font-size: 0.9rem;
        margin-bottom: 5px;
    }

    .summary-value {
        display: block;
        font-size: 1.5rem;
        font-weight: bold;
        color: #667eea;
    }

    .formation-selector {
        margin-bottom: 30px;
        text-align: center;
    }

    .formation-selector label {
        display: block;
        margin-bottom: 10px;
        color: #b0b3b8;
        font-size: 1.1rem;
    }

    .formation-buttons {
        display: flex;
        justify-content: center;
        gap: 10px;
        flex-wrap: wrap;
    }

    .formation-btn {
        padding: 10px 20px;
        background: rgba(255,255,255,0.05);
        border: 2px solid rgba(103,126,234,0.3);
        border-radius: 8px;
        color: #b0b3b8;
        cursor: pointer;
        transition: all 0.3s ease;
        font-size: 1rem;
        font-weight: bold;
    }

    .formation-btn:hover {
        background: rgba(103,126,234,0.2);
        border-color: #667eea;
        color: white;
    }

    .formation-btn.active {
        background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
        border-color: transparent;
        color: white;
    }

    .team-pitch {
        background: linear-gradient(to bottom, #2d5016, #3a6b1e);
        border-radius: 16px;
        padding: 30px 20px;
        margin-bottom: 30px;
        position: relative;
        min-height: 500px;
    }

    .pitch-background {
        background: repeating-linear-gradient(
            to bottom,
            transparent,
            transparent 50px,
            rgba(255,255,255,0.05) 50px,
            rgba(255,255,255,0.05) 100px
        );
        border: 3px solid rgba(255,255,255,0.1);
        border-radius: 12px;
        padding: 20px;
        min-height: 450px;
        display: flex;
        flex-direction: column;
        justify-content: space-between;
    }

    .position-row {
        display: flex;
        justify-content: center;
        gap: 20px;
        margin: 15px 0;
    }

    .position-slot {
        position: relative;
    }

    .player-card {
        width: 90px;
        height: 100px;
        background: linear-gradient(135deg, #1a1a2e, #16213e);
        border: 2px solid rgba(103,126,234,0.5);
        border-radius: 12px;
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        cursor: pointer;
        transition: all 0.3s ease;
        position: relative;
        overflow: hidden;
    }

    .player-card.empty {
        background: rgba(0,0,0,0.4);
        border: 2px dashed rgba(255,255,255,0.3);
    }

    .player-card:hover {
        transform: translateY(-5px);
        box-shadow: 0 10px 20px rgba(103,126,234,0.5);
        border-color: #667eea;
    }

    .player-card.selected {
        background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
        border-color: transparent;
    }

    .position-label {
        font-size: 0.9rem;
        color: #b0b3b8;
        margin-bottom: 5px;
    }

    .add-player-btn {
        font-size: 2rem;
        color: rgba(255,255,255,0.5);
        transition: all 0.3s ease;
    }

    .player-card:hover .add-player-btn {
        color: #667eea;
        transform: scale(1.2);
    }

    .player-info {
        text-align: center;
        padding: 5px;
    }

    .player-name {
        font-size: 0.75rem;
        font-weight: bold;
        color: white;
        margin-bottom: 2px;
        overflow: hidden;
        text-overflow: ellipsis;
        white-space: nowrap;
        width: 80px;
    }

    .player-team {
        font-size: 0.65rem;
        color: #b0b3b8;
        margin-bottom: 2px;
    }

    .player-price {
        font-size: 0.7rem;
        color: #4ade80;
        font-weight: bold;
    }

    .remove-player {
        position: absolute;
        top: 2px;
        right: 2px;
        background: rgba(239,68,68,0.8);
        border: none;
        border-radius: 50%;
        width: 20px;
        height: 20px;
        color: white;
        cursor: pointer;
        font-size: 0.8rem;
        display: none;
        align-items: center;
        justify-content: center;
    }

    .player-card.selected:hover .remove-player {
        display: flex;
    }

    .bench-area {
        margin-top: 30px;
        padding-top: 20px;
        border-top: 2px solid rgba(255,255,255,0.1);
    }

    .bench-area h4 {
        text-align: center;
        color: #b0b3b8;
        margin-bottom: 15px;
    }

    .bench-slots {
        display: flex;
        justify-content: center;
        gap: 15px;
    }

    .bench .player-card {
        width: 80px;
        height: 90px;
        opacity: 0.8;
    }

    .team-actions {
        display: flex;
        justify-content: center;
        gap: 15px;
        margin-top: 30px;
        flex-wrap: wrap;
    }

    .action-btn {
        padding: 12px 25px;
        border-radius: 10px;
        border: none;
        font-weight: bold;
        cursor: pointer;
        transition: all 0.3s ease;
        font-size: 1rem;
        display: flex;
        align-items: center;
        gap: 8px;
    }

    .analyze-btn {
        background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
        color: white;
    }

    .save-btn {
        background: linear-gradient(135deg, #10b981 0%, #059669 100%);
        color: white;
    }

    .clear-btn {
        background: linear-gradient(135deg, #ef4444 0%, #dc2626 100%);
        color: white;
    }

    .import-btn {
        background: linear-gradient(135deg, #3b82f6 0%, #2563eb 100%);
        color: white;
    }

    .action-btn:hover {
        transform: translateY(-2px);
        box-shadow: 0 5px 15px rgba(0,0,0,0.3);
    }

    .ai-analysis-panel {
        margin-top: 30px;
        padding: 25px;
        background: rgba(103,126,234,0.1);
        border-radius: 12px;
        border: 1px solid rgba(103,126,234,0.3);
    }

    .ai-analysis-panel h3 {
        margin-bottom: 20px;
        color: #667eea;
        font-size: 1.5rem;
    }

    .analysis-content {
        color: #b0b3b8;
        line-height: 1.8;
    }

    .analysis-section {
        margin-bottom: 20px;
        padding: 15px;
        background: rgba(255,255,255,0.03);
        border-radius: 8px;
    }

    .analysis-section h4 {
        color: #667eea;
        margin-bottom: 10px;
    }

    /* Player Modal Styles */
    .player-modal {
        position: fixed;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        background: rgba(0,0,0,0.8);
        z-index: 1000;
        display: flex;
        align-items: center;
        justify-content: center;
        backdrop-filter: blur(5px);
    }

    .modal-content {
        background: linear-gradient(135deg, #1a1a2e 0%, #16213e 100%);
        border-radius: 20px;
        width: 90%;
        max-width: 800px;
        max-height: 80vh;
        overflow: hidden;
        box-shadow: 0 20px 40px rgba(0,0,0,0.5);
    }

    .modal-header {
        display: flex;
        justify-content: space-between;
        align-items: center;
        padding: 20px;
        border-bottom: 1px solid rgba(255,255,255,0.1);
        background: rgba(103,126,234,0.1);
    }

    .modal-header h3 {
        color: white;
        font-size: 1.5rem;
    }

    .modal-close {
        background: none;
        border: none;
        color: #b0b3b8;
        font-size: 2rem;
        cursor: pointer;
        transition: color 0.3s ease;
    }

    .modal-close:hover {
        color: #ef4444;
    }

    .modal-body {
        padding: 20px;
        max-height: calc(80vh - 80px);
        overflow-y: auto;
    }

    .player-search {
        display: flex;
        gap: 10px;
        margin-bottom: 20px;
        flex-wrap: wrap;
    }

    .player-search input,
    .player-search select {
        flex: 1;
        min-width: 150px;
        padding: 10px;
        background: rgba(255,255,255,0.05);
        border: 1px solid rgba(255,255,255,0.2);
        border-radius: 8px;
        color: white;
        font-size: 1rem;
    }

    .player-search input::placeholder {
        color: rgba(255,255,255,0.5);
    }

    .player-list {
        display: grid;
        grid-template-columns: repeat(auto-fill, minmax(180px, 1fr));
        gap: 15px;
    }

    .player-list-item {
        background: rgba(255,255,255,0.05);
        border: 2px solid rgba(103,126,234,0.3);
        border-radius: 12px;
        padding: 15px;
        cursor: pointer;
        transition: all 0.3s ease;
        text-align: center;
    }

    .player-list-item:hover {
        background: rgba(103,126,234,0.2);
        border-color: #667eea;
        transform: translateY(-3px);
    }

    .player-list-item.disabled {
        opacity: 0.5;
        cursor: not-allowed;
    }

    .player-list-name {
        font-weight: bold;
        color: white;
        margin-bottom: 5px;
    }

    .player-list-team {
        color: #b0b3b8;
        font-size: 0.9rem;
        margin-bottom: 5px;
    }

    .player-list-price {
        color: #4ade80;
        font-weight: bold;
        margin-bottom: 5px;
    }

    .player-list-points {
        color: #667eea;
        font-size: 0.9rem;
    }

    /* Responsive Design */
    @media (max-width: 768px) {
        .team-picker-section {
            padding: 15px;
        }

        .section-header h2 {
            font-size: 1.8rem;
        }

        .team-summary {
            flex-direction: column;
            gap: 15px;
        }

        .player-card {
            width: 70px;
            height: 80px;
        }

        .bench .player-card {
            width: 60px;
            height: 70px;
        }

        .position-row {
            gap: 10px;
        }

        .team-pitch {
            padding: 15px 10px;
        }

        .player-list {
            grid-template-columns: repeat(auto-fill, minmax(140px, 1fr));
        }
    }
</style>

