        text-align: center;
        padding: 5px;
    }

    .player-name {
        font-size: 0.75rem;
        font-weight: bold;
        color: white;
        margin-bottom: 2px;
        overflow: hidden;
        text-overflow: ellipsis;
        white-space: nowrap;
        width: 80px;
    }

    .player-team {
        font-size: 0.65rem;
        color: #b0b3b8;
        margin-bottom: 2px;
    }

    .player-price {
        font-size: 0.7rem;
        color: #4ade80;
        font-weight: bold;
    }

    .remove-player {
        position: absolute;
        top: 2px;
        right: 2px;
        background: rgba(239,68,68,0.8);
        border: none;
        border-radius: 50%;
        width: 20px;
        height: 20px;
        color: white;
        cursor: pointer;
        font-size: 0.8rem;
        display: none;
        align-items: center;
        justify-content: center;
    }

    .player-card.selected:hover .remove-player {
        display: flex;
    }

