        text-align: center;
        color: #b0b3b8;
        margin-bottom: 15px;
    }

    .bench-slots {
        display: flex;
        justify-content: center;
        gap: 15px;
    }

    .bench .player-card {
        width: 80px;
        height: 90px;
        opacity: 0.8;
    }

    .team-actions {
