        box-shadow: 0 20px 40px rgba(0,0,0,0.5);
    }

    .modal-header {
        display: flex;
        justify-content: space-between;
        align-items: center;
        padding: 20px;
        border-bottom: 1px solid rgba(255,255,255,0.1);
        background: rgba(103,126,234,0.1);
    }

    .modal-header h3 {
        color: white;
        font-size: 1.5rem;
    }

    .modal-close {
