        border-radius: 8px;
        color: white;
        font-size: 1rem;
    }

    .player-search input::placeholder {
        color: rgba(255,255,255,0.5);
    }

    .player-list {
        display: grid;
        grid-template-columns: repeat(auto-fill, minmax(180px, 1fr));
        gap: 15px;
    }

    .player-list-item {
        background: rgba(255,255,255,0.05);
        border: 2px solid rgba(103,126,234,0.3);
        border-radius: 12px;
        padding: 15px;
        cursor: pointer;
        transition: all 0.3s ease;
        text-align: center;
    }

    .player-list-item:hover {
        background: rgba(103,126,234,0.2);
        border-color: #667eea;
        transform: translateY(-3px);
    }

    .player-list-item.disabled {
        opacity: 0.5;
        cursor: not-allowed;
    }

    .player-list-name {
        font-weight: bold;
        color: white;
        margin-bottom: 5px;
    }

    .player-list-team {
        color: #b0b3b8;
        font-size: 0.9rem;
        margin-bottom: 5px;
    }

    .player-list-price {
        color: #4ade80;
        font-weight: bold;
        margin-bottom: 5px;
    }

    .player-list-points {
        color: #667eea;
        font-size: 0.9rem;
    }

    /* Responsive Design */
    @media (max-width: 768px) {
        .team-picker-section {
            padding: 15px;
        }

        .section-header h2 {
            font-size: 1.8rem;
        }

        .team-summary {
            flex-direction: column;
            gap: 15px;
        }

        .player-card {
            width: 70px;
            height: 80px;
        }

        .bench .player-card {
            width: 60px;
            height: 70px;
        }

        .position-row {
            gap: 10px;
        }

        .team-pitch {
            padding: 15px 10px;
        }

        .player-list {
            grid-template-columns: repeat(auto-fill, minmax(140px, 1fr));
        }
    }
</style>

