  5051‚Üí            filterPlayers() {
  5052‚Üí                const searchTerm = document.getElementById('player-search-input')?.value.toLowerCase() || '';
  5053‚Üí                const teamFilter = document.getElementById('team-filter')?.value || '';
  5054‚Üí                const sortBy = document.getElementById('price-sort')?.value || 'price-desc';
  5055‚Üí
  5056‚Üí                let players = this.playerDatabase;
  5057‚Üí
  5058‚Üí                // Filter by position first
  5059‚Üí                if (this.currentPosition === 'BENCH') {
  5060‚Üí                    players = players.filter(p => p.position !== 'GK');
  5061‚Üí                } else if (this.currentPosition && this.currentPosition !== 'ALL') {
  5062‚Üí                    players = players.filter(p => p.position === this.currentPosition);
  5063‚Üí                }
  5064‚Üí
  5065‚Üí                // Search filter
  5066‚Üí                if (searchTerm) {
  5067‚Üí                    players = players.filter(p => 
  5068‚Üí                        p.name.toLowerCase().includes(searchTerm) ||
  5069‚Üí                        p.team.toLowerCase().includes(searchTerm)
  5070‚Üí                    );
  5071‚Üí                }
  5072‚Üí
  5073‚Üí                // Team filter
  5074‚Üí                if (teamFilter) {
  5075‚Üí                    players = players.filter(p => p.team === teamFilter);
  5076‚Üí                }
  5077‚Üí
  5078‚Üí                // Sort
  5079‚Üí                switch(sortBy) {
  5080‚Üí                    case 'price-asc':
  5081‚Üí                        players.sort((a, b) => a.price - b.price);
  5082‚Üí                        break;
  5083‚Üí                    case 'price-desc':
  5084‚Üí                        players.sort((a, b) => b.price - a.price);
  5085‚Üí                        break;
  5086‚Üí                    case 'points-desc':
  5087‚Üí                        players.sort((a, b) => b.points - a.points);
  5088‚Üí                        break;
  5089‚Üí                    case 'form-desc':
  5090‚Üí                        players.sort((a, b) => b.form - a.form);
  5091‚Üí                        break;
  5092‚Üí                }
  5093‚Üí
  5094‚Üí                this.displayPlayers(players);
  5095‚Üí            }
