  5321‚Üí            generateRecommendations() {
  5322‚Üí                let recommendations = '<ul>';
  5323‚Üí                const stats = this.calculateTeamStats();
  5324‚Üí                const remainingBudget = this.calculateRemainingBudget();
  5325‚Üí
  5326‚Üí                // Check if team is complete
  5327‚Üí                if (this.selectedPlayers.length < 15) {
  5328‚Üí                    recommendations += `<li>‚ö†Ô∏è Complete your squad by adding ${15 - this.selectedPlayers.length} more players</li>`;
  5329‚Üí                }
  5330‚Üí
  5331‚Üí                // Check position requirements
  5332‚Üí                if (stats.gk < 2) {
  5333‚Üí                    recommendations += `<li>Add ${2 - stats.gk} more goalkeeper(s)</li>`;
  5334‚Üí                }
  5335‚Üí                if (stats.def < 5) {
  5336‚Üí                    recommendations += `<li>Add ${5 - stats.def} more defender(s)</li>`;
  5337‚Üí                }
  5338‚Üí                if (stats.mid < 5) {
  5339‚Üí                    recommendations += `<li>Add ${5 - stats.mid} more midfielder(s)</li>`;
  5340‚Üí                }
  5341‚Üí                if (stats.fwd < 3) {
  5342‚Üí                    recommendations += `<li>Add ${3 - stats.fwd} more forward(s)</li>`;
  5343‚Üí                }
  5344‚Üí
  5345‚Üí                // Budget recommendations
  5346‚Üí                if (remainingBudget < 0) {
  5347‚Üí                    recommendations += `<li>‚ùå Over budget by ¬£${Math.abs(remainingBudget).toFixed(1)}m - remove or downgrade players</li>`;
  5348‚Üí                } else if (remainingBudget > 10 && this.selectedPlayers.length === 15) {
  5349‚Üí                    recommendations += `<li>üí∞ Consider upgrading players with ¬£${remainingBudget.toFixed(1)}m remaining</li>`;
  5350‚Üí                }
  5351‚Üí
  5352‚Üí                // Premium player check
  5353‚Üí                const premiumPlayers = this.selectedPlayers.filter(p => p.price >= 10);
  5354‚Üí                if (premiumPlayers.length === 0) {
  5355‚Üí                    recommendations += `<li>Consider adding at least one premium player (¬£10m+) for captaincy options</li>`;
  5356‚Üí                } else if (premiumPlayers.length > 3) {
  5357‚Üí                    recommendations += `<li>You have ${premiumPlayers.length} premium players - consider more budget options for balance</li>`;
  5358‚Üí                }
  5359‚Üí
  5360‚Üí                // Team concentration
  5361‚Üí                const teamCounts = {};
  5362‚Üí                this.selectedPlayers.forEach(p => {
  5363‚Üí                    teamCounts[p.team] = (teamCounts[p.team] || 0) + 1;
  5364‚Üí                });
  5365‚Üí
  5366‚Üí                for (let team in teamCounts) {
  5367‚Üí                    if (teamCounts[team] === 3) {
  5368‚Üí                        recommendations += `<li>‚ö†Ô∏è You have 3 players from ${team} (maximum allowed)</li>`;
  5369‚Üí                    }
  5370‚Üí                }
  5371‚Üí
  5372‚Üí                recommendations += '</ul>';
  5373‚Üí                return recommendations;
  5374‚Üí            }
