  5376‚Üí            suggestCaptains() {
  5377‚Üí                return [...this.selectedPlayers]
  5378‚Üí                    .filter(p => p.position !== 'GK')
  5379‚Üí                    .sort((a, b) => {
  5380‚Üí                        const scoreA = (parseFloat(a.form) * 0.5) + (a.points * 0.3) + (parseFloat(a.price) * 0.2);
  5381‚Üí                        const scoreB = (parseFloat(b.form) * 0.5) + (b.points * 0.3) + (parseFloat(b.price) * 0.2);
